function fn_OnLoadCompleted(obj) {
	gfn_initForm();	
	fn_clear();

	div_pop.ed_pjt.Text   = PRE_PJT;
	div_pop.ed_hno.Text   = PRE_HNO;
	div_pop.ed_seqno.Text = PRE_SEQNO;

	fn_query();

}
