********************************************************************************/
function fn_query() {
	if(gfn_isNull(div_pop.ed_pjt, true, Static0.Text)) return;
	if(gfn_isNull(div_pop.ed_hno, true, Static2.Text)) return;
	ds_detail.DeleteAll();

	POSID = div_pop.ed_pjt.Text + div_pop.ed_hno.Text;
	ds_cond.SetColumn(0, "POSID", POSID);

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps11:PS1101003A_S1");
	tit_addSearchActionInfo("ps11:PS1110002B_S1");
	tit_addSearchActionInfo("ps11:PS1110002B_S2");

	// 조회 화면
	// tit_addSearchActionInfo("ps11:PS1101003B_S1");
	// tit_addSearchActionInfo("ps11:PS1101003B_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_detail=ds_detail"
        , ""
        , "fn_afterQuery"
        , "" //true
        , ""
        , true);


}
