function fn_query() {
	ds_list2.Clear();

	tit_clearActionInfo();
	if(cb_period.Value == "B")
	{
	 tit_addSearchActionInfo("ps11:PS1110003A_S1");		                         	
	}
	else
	{
	 tit_addSearchActionInfo("ps11:PS1110003A_S6");		
	}
	 tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true);



     ed_FileName.value = '';

//alert(ds_list.SaveXML());	                

}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	ds_list2.ClearData();
	if(ds_list.RowCount() > 0) fn_query2(0);
}



/********************************************************************************
* 기      능   : grid double click 시 조회 
********************************************************************************/
function fn_query2(nRow) {
	ds_list2.ClearData();

//alert(ds_list.SaveXML());

	ds_cond2.ClearData();
	ds_cond2.AddRow();
	//ds_cond2.setColumn(0, "GBN", ds_list.GetColumn(nRow,"GBN"));
	ds_cond2.setColumn(0, "QTNUM", ds_list.GetColumn(nRow,"QTNUM"));
	ds_cond2.setColumn(0, "QTDAT", ds_list.GetColumn(nRow,"QTDAT"));
	ds_cond2.setColumn(0, "QTSER", ds_list.GetColumn(nRow,"QTSER"));
	ds_cond2.setColumn(0, "VBELN", ds_list.GetColumn(nRow,"VBELN"));

	//trace("ds_cond2:"+ds_cond2.SaveXML());
	ds_rad_hogi.ClearData();
	if(Length(ds_list.GetColumn(nRow,"VBELN"))>0) {
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps11:PS1110003A_S8", null, true);
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond2"
			, "ds_rad_hogi=ds_rad_hogi"
			, ""
			, "fn_afterQuery2"
			, ""
			, ""
			, true);
	} else {
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps11:PS1110003A_S7", null, true);
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond2"
			, "ds_rad_hogi=ds_rad_hogi"
			, ""
			, "fn_afterQuery2"
			, ""
			, ""
			, true);
	}

	//trace("ds_rad_hogi:"+ds_rad_hogi.SaveXML());
	// 호기1개 1대 짜리만 자동도면 생성
	//if(ds_rad_hogi.GetRowCount() > 0) {
	//if(ds_rad_hogi.GetRowCount() == 1) {
		Button2.Enable =true;
		if(G_DPT1=='10' || G_DPT1=='F6' || G_USER_ID == 'TEST12') {
			Button3.Enable =true;
		} else {
			Button3.Enable =false;
		}		
	//} else {
	//	Button2.Enable =false;
	//	Button3.Enable =false;
	//}

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps11:PS1110003A_S2", null, true);
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery2"
        , ""
        , ""
        , true);
}

