function btn_openFile_OnClick(obj)
{
	fileDialog.open();
  //  fileDailog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length(fileDialog.FileName ) == 0 ) {
		return;
	}

	var fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "10 Mbytes" );	
		return;
	}

	/*
	//한글제목 입력금지(한글제목깨짐)
	for( var i = 0; i < length( fileDialog.FileName); i++ ) {
		if ( lengthb(charAt( fileDialog.FileName, i)) == 2 ) {
			gfn_showMsg("CW00213");
			return;
		}
	}
	*/

	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}

	ed_FileName.value = fileDialog.FileName;

	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		if(ds_list.GetColumn(i, "CHECK")==1)
		{

			// 파일정보
			//if(length(ed_FileName.value) > 0 )
			//{
				ds_list.setColumn(i, "FNAME", fileDialog.FileName);
				ds_list.setColumn(i, "FSIZE", fileSize);
				ds_list.setColumn(i, "FPATH", fileDialog.FilePath);
				//ds_list.setColumn(i, "FPATH", fileDialog.FilePath);


			//	ds_list.setColumn(i, "GBN", cb_gbn.Value);
			//} 

		}
	}

//alert(ds_list.SaveXML());	

//Alert("FNAME : " + ds_list.GetColumn(0, "FNAME"));
//Alert("FSIZE : " + ds_list.GetColumn(0, "FSIZE"));
//Alert("FPATH : " + ds_list.GetColumn(0, "FPATH"));
//Alert("FNAME : " + fileDialog.FileName);
//Alert("FSIZE : " + fileSize);
//Alert("ds_list : " + ds_list.SaveXML());
//alert(ds_cond.SaveXML());	 

}


/********************************************************************************
* 기      능   :저장
********************************************************************************/
function fn_save() {


    //  단일 1대 승강기인경우
    //  고객 요청에 의해서 나중에 해당 체크로직 적용
    /*
    var suvstCnt =  ds_rad_hogi.CaseCount("SUVST=='Y'");
//    if(ds_rad_hogi.GetRowCount()== 1) {
      if(ds_rad_hogi.GetRowCount() != suvstCnt ) {
	      gfn_openMsgWin("실측등록 작업 완료 후 이미지 파일(pdf)을 등록할 수 있습니다.","W");
          return false;
//    }
    */

	if(gfn_isNull(ed_FileName, true, st_insuName.Value)) return false; // 첨부파일

  //  if(st_insuName.Value = '')

	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}


	ds_list.Filter("CHECK==1");
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();	

	if(fn_checkSave()){

//Alert("httpFile : " + httpFile );	
//alert("FPATH : " + ds_list_tmp.getColumn(0, "FPATH"));	
//alert("FNAME : " + ds_list_tmp.getColumn(0, "FNAME"));	
//alert("fileName : " + fileName);


		var fileName = "";
		//fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_tmp.getColumn(0, "FNAME");
		fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_tmp.getColumn(0, "QTNUM");

		var arr = tit_fileWrite(httpFile
						, "PS"
						, ds_list_tmp.getColumn(0, "FPATH")
						, ds_list_tmp.getColumn(0, "FNAME")
						, fileName// 실제 db에 저장될 파일 이름
						, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
						, "");    // ds_list_tmp.getColumn(i, "CS108_QAD") );//삭제할 이전 파일명 

//alert("xx");						
///alert("arr[0] : " + arr[0]);	
//alert("arr[1] : " + arr[1]);
//alert("arr[2] : " + arr[2]);

//alert(ds_list_tmp.SaveXML());			
		if ( arr[0] == "S" ) 
		{  
			for(var i=0; i<ds_list_tmp.GetRowCount(); i++)
			{ 
				ds_list_tmp.setColumn(i, "FNAME_APP", arr[2]);

				//=======update/insert (마스터)구분=================
				ds_cond3.ClearData();
				ds_cond3.AddRow();
				ds_cond3.setColumn(0, "QTNUM", ds_list_tmp.GetColumn(i, "QTNUM"));
				ds_cond3.setColumn(0, "QTDAT", ds_list_tmp.GetColumn(i, "QTDAT"));
				ds_cond3.setColumn(0, "FNAME", ds_list_tmp.GetColumn(i, "FNAME"));
				ds_cond3.setColumn(0, "BIGO", ds_list_tmp.GetColumn(i, "BIGO"));



				tit_clearActionInfo();
				tit_addSelectExActionInfo("ps11:PS1110003A_S3", "ps11:PS1110003A_I2", "ps11:PS1110003A_U1");				                           
				tit_callService(
					""
					, ""
					, "ds_cond3=ds_cond3"
					, ""
					, ""
					, ""
					, ""
					, ""
					, true  // ""

				);

			}
			//디테일 INSERT
			tit_clearActionInfo();
			tit_addMultiActionInfo("ps11:PS1110003A_I1");		                       
			tit_callService(
			  ""
			, ""
			, "ds_list_tmp=ds_list_tmp"
			, ""
			, ""
			, "fn_afterSave" 
			, ""
			, ""
			, true);

		}
		else if ( arr[0] != "S" )
		{  
			gfn_showMsg("CW00035"); 
			return;
		}



	}


}

/********************************************************************************
* 기      능   : 저장전에 자료 검증 
********************************************************************************/
function fn_checkSave()
{
  // 해당 호기가 자동도면 대상인 경우 이고 실적등록이 완료되지 않은 경우 메시지 처리

//----- 저장전 확장자 확인  -------------------------------

 var thumbext = ed_FileName.value; //파일을 추가한 input 값

     thumbext = substr(thumbext, thumbext.indexOf(".") + 1); //확정자값 가져오기 

     thumbext = ToLower(thumbext); //소문자로 변경


  if(thumbext != "pdf"){ // 확장자를 확인

     global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
	 global.FRM_BOTTOM.st_msg.color = "yellow";       
	 global.FRM_BOTTOM.st_msg.value = "이미지 파일(pdf)만 등록 가능합니다.";
	 return false;
  }
//----------------------------------------------------------


	for(var i=0; i<ds_list_tmp.GetRowCount(); i++)
	{


		ds_cond3.ClearData();
		ds_cond3.AddRow();
		ds_cond3.setColumn(0, "QTNUM", ds_list_tmp.GetColumn(i, "QTNUM"));
		ds_cond3.setColumn(0, "QTDAT", ds_list_tmp.GetColumn(i, "QTDAT"));
		ds_cond3.setColumn(0, "FNAME", ds_list_tmp.GetColumn(i, "FNAME"));

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps11:PS1110003A_S4");		                         	
		tit_callService(
			""
			, ""
			, "ds_cond3=ds_cond3"
			, "ds_list3=ds_list3"
			, ""
			, ""
			, ""
			, ""
			, true);

//Alert("CNT : " + ds_list_tmp.GetColumn(0, "CNT") );

		if( ds_list3.GetColumn(0, "CNT") > 0 )
		{
			global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
			global.FRM_BOTTOM.st_msg.color = "yellow";       
			global.FRM_BOTTOM.st_msg.value = "같은 견적번호에 동일한 파일이 존재합니다.";
			return false;
		}

	}



	return true;
}



