/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) 
	{		
		gfn_showMsg("CE00001");		
	} 
	else 
	{
		gfn_showMsg("CI00003");
		ds_list_tmp.DeleteAll();
        ed_FileName.value = '';
		//fn_query();
	}

	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps11:PS1110003A_S5");			
	tit_callService(
	  ""
	, ""
	, "ds_cond2=ds_cond2"
	, "ds_trans=ds_trans"
	, ""
	, ""  //"fn_afterQuery"
	, "" //true
	, ""
	, true);	

	//====================쪽지전송 시작===============================		
		var arg = "";		
		var param = ""; 
		var lv_mesgdesc = "";
		var lv_today = gfn_getCurrDate();

		lv_mesgdesc = '견적번호 [' +  ds_trans.GetColumn(0,"QTNUM") +']('+ ds_trans.GetColumn(0,"GSNAM") +') 파일이 입력되었습니다.\n' 
		             + Substr(lv_today,0,4) + '.' + Substr(lv_today,4,2) + '.' + Substr(lv_today,6,2) + '일에 실측이 완료되었으므로 영업사양 신속히 전송 바랍니다.';
		ds_trans.SetColumn(0, "MESGDESC", lv_mesgdesc);

		ds_trans.setcolumn(0, "MESGDESC", lv_mesgdesc);


		// #1. 영업기술 담당자 전송
		if(length(ds_trans.GetColumn(0,"KUNNR")) > 0)
		{
		  ds_trans.setcolumn(0, "MESGACCP", substr(ds_trans.GetColumn(0,"KUNNR"),1,7));
		  perno = ds_trans.GetColumn(0, "KUNNR_MAIL");
		  ds_trans.SetColumn(0, "PERNO",  perno);

		  //perno = 'helco' + SubStr(ds_trans.GetColumn(0,"KUNNR"),1,7) + '@hdel.co.kr';
		  //ds_trans.setcolumn(0, "MESGACCP", "2022702");
		  //perno = 'helco2022702@hdel.co.kr';
		  //ds_trans.SetColumn(0, "PERNO",  perno);
		}
		else
		{   //담당자 미정시 윤현휴 GJ (기술영업부 파트장 defult / 이현철 GJ 요청)
		    // 담당자 미정 시 이현철 책임으로 변경처리 (2020.08.18 수정 요청 - 이현철 책임님 요청)
		  ds_trans.setcolumn(i, "MESGACCP", '2017954');
		  //ds_trans.setcolumn(0, "MESGACCP", "2022702");
		  perno = 'helco2017954@hdel.co.kr';
		  ds_trans.SetColumn(0, "PERNO",  perno);
		}

		// #2. 영업 담당자 전송
		//ds_trans.SetColumn(0, "PERNO2",  ds_trans.GetColumn(0, "SD_MAIL1"));

		// #3. 영업 관리 담당자 전송
		//ds_trans.SetColumn(0, "PERNO3",  ds_trans.GetColumn(0, "SD_MAIL2"));

     fn_trans();
}



