/********************************************************************************
* 기      능   : 실측등록 
********************************************************************************/
function btn_rap_cal_OnClick(obj)
{

	if(ds_list.count >0 ) {	
		var arg = "";
		arg= "QTNUM=" + quote(ds_list.getColumn(grd_list.CurrentRow,"QTNUM"));  // 견적번호
		arg= arg + ", QTSER=" + quote(ds_list.getColumn(grd_list.CurrentRow,"QTSER")); // 견적차수
		arg= arg + ", GSNAM=" + quote(ds_list.getColumn(grd_list.CurrentRow,"GSNAM")); // 공사명
		//arg= arg + ", PM=" + quote(ds_list.getColumn(grd_list.CurrentRow,"PM"));
		arg= arg + ", NAME1=" + quote(ds_list.getColumn(grd_list.CurrentRow,"NAME1")); // 기술영업담당
		arg= arg + ", NAMET=" + quote(ds_list.getColumn(grd_list.CurrentRow,"NAMET")); // PM
		arg= arg + ", VBELN=" + quote(ds_list.getColumn(grd_list.CurrentRow,"VBELN")); // 수주번호 ? 프로젝트번호
		arg= arg + ", ZKUNNR=" + quote(ds_list.getColumn(grd_list.CurrentRow,"ZKUNNR")); // 영업담당
		//trace(arg);
		if(Length(ds_list.getColumn(grd_list.CurrentRow,"QTNUM")) == 0 ) {
		    alert("실측등록 견적번호를 선택해 주십시요");
		    return false;
		} else {
		    var arr = gfn_openPopupUrl("PS11::PS1120001A.xml", true, arg);
		}
	}else {
		alert("데이터를 선택후 버튼을 클릭 하십시오");
		return false;
	}
}

