// 견적번호 기준 쿼리
function fn_query_qtnum() {


	ds_cond_qtnum.ClearData();
	ds_qtnum_list.ClearData();
	ds_cond_qtnum.AddRow();
	ds_cond_qtnum.setColumn(0, "QTNUM", QTNUM);
	//TRACE(ds_cond_qtnum.SaveXML());
	//ds_cond.setColumn(0, "BIGO", ds_list_tmp.GetColumn(i, "BIGO"))

	tit_clearActionInfo();
	        //TRACE("ds_qtnum_list query end 1 " );
	 tit_addSearchActionInfo("ps11:PS1120001C_S1");
        //TRACE("ds_qtnum_list query end 2" );  
  	 tit_callService(
        ""
        , ""
        , "ds_cond_qtnum=ds_cond_qtnum"
        , "ds_qtnum_list=ds_qtnum_list"
        , ""
        , ""
        , ""
        , ""
        , true);
        //TRACE("ds_qtnum_list query end 3 " );        

        //trace("result : " + ds_qtnum_list.SaveXML());

        //TRACE("ds_qtnum_list query end " );

        //TRACE(ds_rad_list.SaveXML());

}

