        , ""
        , ""
        , ""
        , true);

        //trace("result : " + ds_vbeln_list.SaveXML());

        //TRACE("ds_vbeln_list query end " );



}



/********************************************************************************
* 기      능   : 복사
********************************************************************************/

function fn_copy() {


	tit_clearActionInfo();


	//trace(ds_qtnum_list.SaveXML());

	//alert("복사버튼 클릭 하셨네요...");

	// from 수주 기준 자료와 견적기준 자료들 중에서 한건의 qtnum,qtser,qtseq,suveln, suvsn 값을 구한다.
	var from_selected_list="";
	var from_selected_line;
	var from_selected_cnt=0;
	for(var i=0; i<ds_vbeln_list.count; i++) {
			if(ds_vbeln_list.getColumn(i,"FLAG") == "1") {
			    from_selected_list="ds_vbeln_list";
				from_selected_line=i;
				from_selected_cnt++;


			}
	}

	for(var i=0; i<ds_qtnum_list.count; i++) {
	    //trace("value : " + ds_qtnum_list.getColumn(i,"FLAG"));
		if(ds_qtnum_list.getColumn(i,"FLAG") == "1") {
			from_selected_list="ds_qtnum_list";
			from_selected_line=i;
			from_selected_cnt++;
			//trace(from_selected_cnt);

		}
	}

	//trace(ds_qtnum_list.SaveXML();

	//trace("count: " + ds_rad_list.count);

	if(from_selected_cnt == 0) {
		alert("FROM 값을 선택해 주십시오.");
		return false;
	}

	if(from_selected_cnt > 1) {
		alert("FROM 값을 하나만 선택해 주십시오.");
		return false;
	}

	//trace("from_selected_line: " + from_selected_line);

	var to_selected_list="";
	var to_selected_line;
	var to_selected_cnt=0;	
	//trace("count: " + ds_rad_list.count);

	ds_cond_copy.ClearData();


	// 동일 항목이 있는지 검증 한다. 
	// 동일 항목의 경우 복사하지 않고 제외해달라는 메시지를 뿌린다. 
	for(var i=0; i<ds_rad_list.count; i++) {

		//trace("selected : " +  from_selected_list);

		if(ds_rad_list.getColumn(i,"FLAG") == "1") {

				var OldStr = "";
				if(from_selected_list=="ds_vbeln_list") {
					//trace("selected 1 : " +  from_selected_list);
					OldStr = OldStr +  "^" + ds_vbeln_list.getColumn(from_selected_line,"QTNUM");
					trace("selected 2 : " +  from_selected_list);
					OldStr = OldStr +  "^" + ds_vbeln_list.getColumn(from_selected_line,"QTSER");
					OldStr = OldStr +  "^" + ds_vbeln_list.getColumn(from_selected_line,"QTSEQ");
					OldStr = OldStr +  "^" + ds_vbeln_list.getColumn(from_selected_line,"VBELN");
					OldStr = OldStr +  "^" + ds_vbeln_list.getColumn(from_selected_line,"SUVSN");
					OldStr = OldStr +  "^" + ds_vbeln_list.getColumn(from_selected_line,"HOGI");
				}
				//trace("Old Str : " + OldStr);				
				if(from_selected_list=="ds_qtnum_list") {
					//trace("Here");
					OldStr = OldStr +  "^" + ds_qtnum_list.getColumn(from_selected_line,"QTNUM");
					OldStr = OldStr +  "^" + ds_qtnum_list.getColumn(from_selected_line,"QTSER");
					OldStr = OldStr +  "^" + ds_qtnum_list.getColumn(from_selected_line,"QTSEQ");
					OldStr = OldStr +  "^" + ds_qtnum_list.getColumn(from_selected_line,"VBELN");
					OldStr = OldStr +  "^" + ds_qtnum_list.getColumn(from_selected_line,"SUVSN");
					OldStr = OldStr +  "^" +  "";
				}

				//trace(OldStr);
				var NewStr = "";
				NewStr = NewStr +  "^" + ds_rad_list.getColumn(i,"QTNUM");
				NewStr = NewStr +  "^" + ds_rad_list.getColumn(i,"QTSER");
				NewStr = NewStr +  "^" + ds_rad_list.getColumn(i,"QTSEQ");
				NewStr = NewStr +  "^" + ds_rad_list.getColumn(i,"VBELN");
				NewStr = NewStr +  "^" + ds_rad_list.getColumn(i,"SUVSN");
				NewStr = NewStr +  "^" + ds_rad_list.getColumn(i,"HOGI");

				//trace(OldStr);
				//trace(NewStr);

				if(NewStr==OldStr) {

					alert("복사대상에서 동일한 자료를 제외해 주십시오" + "\n견적번호 : " + ds_rad_list.getColumn(i,"QTNUM") + "\n견적차수 : " + ds_rad_list.getColumn(i,"QTSER") + "\n견적순번 : " + ds_rad_list.getColumn(i,"QTSEQ")  + "\n수주번호 : " + ds_rad_list.getColumn(i,"VBELN")  + "\n호기 : " + ds_rad_list.getColumn(i,"HOGI"));
					return false;
				}
		}


	}


	var tmpSUVSN="";
	for(var i=0; i<ds_rad_list.count; i++) {
		var tmpSUVSN="";
		//trace("value " +i+ " : " + ds_rad_list.getColumn(i,"FLAG"));
		if(ds_rad_list.getColumn(i,"FLAG") == "1") {
			ds_cond_copy.ClearData();


			to_selected_list="ds_rad_list";
			to_selected_line=i;
			to_selected_cnt++;
			//trace("to : " + to_selected_cnt);
			//trace("list : " + from_selected_list);
			var aRow = ds_cond_copy.AddRow();
				if(from_selected_list=="ds_vbeln_list") {
					ds_cond_copy.setColumn(aRow, "FROM_QTNUM", ds_vbeln_list.getColumn(from_selected_line,"QTNUM"));
					ds_cond_copy.setColumn(aRow, "FROM_QTSER", ds_vbeln_list.getColumn(from_selected_line,"QTSER"));
					ds_cond_copy.setColumn(aRow, "FROM_QTSEQ", ds_vbeln_list.getColumn(from_selected_line,"QTSEQ"));
					ds_cond_copy.setColumn(aRow, "FROM_VBELN", ds_vbeln_list.getColumn(from_selected_line,"VBELN"));
					ds_cond_copy.setColumn(aRow, "FROM_SUVSN", ds_vbeln_list.getColumn(from_selected_line,"SUVSN"));
					ds_cond_copy.setColumn(aRow, "FROM_HOGI", ds_vbeln_list.getColumn(from_selected_line,"HOGI"));
				}
				if(from_selected_list=="ds_qtnum_list") {
					//trace("Here");
					ds_cond_copy.setColumn(aRow, "FROM_QTNUM", ds_qtnum_list.getColumn(from_selected_line,"QTNUM"));
					ds_cond_copy.setColumn(aRow, "FROM_QTSER", ds_qtnum_list.getColumn(from_selected_line,"QTSER"));
					ds_cond_copy.setColumn(aRow, "FROM_QTSEQ", ds_qtnum_list.getColumn(from_selected_line,"QTSEQ"));
					ds_cond_copy.setColumn(aRow, "FROM_VBELN", ds_qtnum_list.getColumn(from_selected_line,"VBELN"));
					//trace("suvsn : " +ds_qtnum_list.getColumn(from_selected_line,"SUVSN"));
					ds_cond_copy.setColumn(aRow, "FROM_SUVSN", ds_qtnum_list.getColumn(from_selected_line,"SUVSN"));
					ds_cond_copy.setColumn(aRow, "FROM_HOGI", "");
				}
				//trace(ds_dummy.SaveXML());
				ds_dummy.ClearData();

				tit_clearActionInfo();
				//tit_addSearchActionInfo("ps11:PS1120002A_S4"); // 실측고유번호 조회
				tit_addSingleActionInfo("ps11:PS1120002A_S4"); // 실측고유번호 조회

				tit_callService(""
								,""
								,"ds_cond=ds_cond"
								,"ds_dummy=ds_dummy"
								,""
								,""
								,""
								,""
							   ,true
							   ,true
							   ,true);					

				tmpSUVSN = ds_dummy.GetColumn(0,"SUVSN"); //tmpSUVSN=fn_get_suvsn();

				//trace("tmpSUVSN : " + i + " : " + tmpSUVSN);
				ds_cond_copy.setColumn(aRow, "TO_QTNUM", ds_rad_list.getColumn(i,"QTNUM"));
				ds_cond_copy.setColumn(aRow, "TO_QTSER", ds_rad_list.getColumn(i,"QTSER"));
				ds_cond_copy.setColumn(aRow, "TO_QTSEQ", ds_rad_list.getColumn(i,"QTSEQ"));
				ds_cond_copy.setColumn(aRow, "TO_VBELN", ds_rad_list.getColumn(i,"VBELN"));
				ds_cond_copy.setColumn(aRow, "TO_SUVSN", tmpSUVSN);
				ds_cond_copy.setColumn(aRow, "TO_HOGI", ds_rad_list.getColumn(i,"HOGI"));
				ds_cond_copy.setColumn(aRow, "TO_BRNDCD", ds_rad_list.getColumn(i,"BRNDCD"));
				ds_cond_copy.setColumn(aRow, "TO_GTYPE", ds_rad_list.getColumn(i,"GTYPE"));
				ds_cond_copy.setColumn(aRow, "TO_ZUSE", ds_rad_list.getColumn(i,"ZUSE"));
				ds_cond_copy.setColumn(aRow, "TO_TYPE1", ds_rad_list.getColumn(i,"TYPE1"));
				ds_cond_copy.setColumn(aRow, "TO_TYPE2", ds_rad_list.getColumn(i,"TYPE2"));


	           // trace("list : " + ds_cond_copy.SaveXML());

				tit_clearActionInfo();

				if(ds_rad_list.getColumn(i,"VBELN")!="") {
					tit_addSingleActionInfo("ps11:PS1120001C_I3"); // 수주실측 등록
				} else {
					tit_addSingleActionInfo("ps11:PS1120001C_I1"); // 견적실측 등록
				}



				tit_callService(""
							   ,""
							   ,"ds_cond_copy=ds_cond_copy"
							   ,""
							   ,""
							   ,""
							   ,true
							   ,true
							   ,true);	

				tit_clearActionInfo();



				if(ds_rad_list.getColumn(i,"VBELN")!="") {
					tit_addSingleActionInfo("ps11:PS1120001C_I4"); // 견적실측 사양 등록
				} else {
					tit_addSingleActionInfo("ps11:PS1120001C_I2"); // 견적실측 사양 등록
				}



				tit_callService(""
							   ,""
							   ,"ds_cond_copy=ds_cond_copy"
							   ,""
							   ,""
							   ,""
							   ,true
							   ,true
							   ,true);					

				tit_clearActionInfo();	

				if(ds_rad_list.getColumn(i,"VBELN")!="") {
					tit_addSingleActionInfo("ps11:PS1120001C_I6"); // 적용층실측 값 등록
				} else {
					tit_addSingleActionInfo("ps11:PS1120001C_I5"); // 적용층실측 값 등록
				}



				tit_callService(""
							   ,""
							   ,"ds_cond_copy=ds_cond_copy"
							   ,""
							   ,""
							   ,""
							   ,true
							   ,true
							   ,true);	




				//fn_query_master();
		        //fn_query_detail();


		}
	}

	//trace("list : " + ds_cond_copy.SaveXML());

	if(to_selected_cnt == 0) {
		alert("TO 값을 1개 이상 선택해 주십시오.");
		return false;
	}


    // 복사 쿼리 실행하기. 	





}

