	tit_clearActionInfo();		
	tit_addSearchActionInfo("ps02:PS0201001A_S32");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"  // ds_pronr=ds_pronr"
		, ""
		, ""   //"fn_afterQueryC"
		, ""
		, ""
		, true);

	//chk_so3.Value = '1';
	//chk_so4.Value = '1';


	ed_tran_f.value = gfn_getAfterDate(gfn_getCurrDate(),-14);
	ed_tran_t.value = gfn_getCurrDate();//gfn_getLeapLastDay(gfn_getCurrDate());



