/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() 
{
    //if ( length(ed_pspid.Text) == 0 and length(ed_tran_f.VALUE) == 0 )
    if ( length(ed_tran_f.VALUE) == 0 )
    {
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		if(ds_cond.GetColumn(0,"PERIOD") == 'A')
	    {
	      global.FRM_BOTTOM.st_msg.value = "전송일은 필수입력값입니다.";
	    } else{
	      global.FRM_BOTTOM.st_msg.value = "착공예정일은 필수입력값입니다.";
	    }
		return;
    }

	//전송일 체크
    if ( !gfn_checkFromToObj(ed_tran_f, ed_tran_t, true, st_date.value, 0, true)   ) 
    {	return; 	}

//alert(ds_cond.SaveXML());	

//====여기서부터    
	tit_clearActionInfo();	
	//tit_addSearchActionInfo("ps02:PS0206003A_S1");  	
	if(ds_cond.GetColumn(0,"PERIOD") == 'A')
	 {
	   tit_addSearchActionInfo("ps11:PS1140001A_S2");  	
	 } else {
	   tit_addSearchActionInfo("ps11:PS1140001A_S3");  	
	 }
	   tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list1=ds_list1"  
        , ""
        , "fn_afterQuery"  
        , ""
        , ""
        , true);


    //=======기술영업담당자만=================
   if (length(ed_perno2.Text) != 0 )
    {
		ds_list1.Filter("NAME1="+quote(ed_perno2.Text));
	}

	//ds_list2.Filter("ZZGUBUN==10 OR ZZGUBUN==11 OR ZZGUBUN==12 OR ZZGUBUN==13 OR ZZGUBUN==14");
	//ds_file.copyF(ds_list2);
	//ds_list2.unFilter();
	//================================================== 

	//ds_list1.Sort("PSPID,POSID,SEQNO",True); 

}



