	tit_addSearchActionInfo("ps02:PS0201001A_S41");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterZzactssChange"
		, true,true);
}


/********************************************************************************
* 기      능   : 조회 Callback  - 사무소 변경시
********************************************************************************/
function fn_afterZzactssChange(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
}


/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() 
{
	// 그리드 저장 대상 로우
	if(ds_list1.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}
	for(var i=0; i<ds_list1.GetRowCount(); i++){
		if(ds_list1.GetColumn(i, "CHECK")==1){

			// 필수 입력 항목	
			if( length(ds_list1.GetColumn(i, "CONF_DATE")) <= 0 && ds_list1.GetColumn(i, "CONF") == 'Y') 
			{
				var arr = [parseInt(i) + 1, "완료여부가 '완료'시 완료일"];
				gfn_showMsg("CW00224", arr);
				return;
			} 
			if( length(ds_list1.GetColumn(i, "CONF_DATE")) > 0 && ds_list1.GetColumn(i, "CONF") != 'Y') 
			{
				var arr = [parseInt(i) + 1, "완료일이 있을시 완료여부는 '완료'선택"];
				gfn_showMsg("CW00224", arr);
				return;
			} 
		}
	}


	ds_list1.Filter("CHECK==1");
	ds_list.Clear();
	ds_list.copyF(ds_list1);
	ds_list1.unFilter();

	tit_clearActionInfo();
	//tit_addMultiActionInfo("ps07:PS0704001A_I1");
	tit_addMultiActionInfo("ps11:PS1108001A_U1");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true);	

}


/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
	    //=========================================//
	    ds_list1.fireevent = false;
		for(var i=0; i<ds_list1.GetRowCount(); i++)
		{
			if(ds_list1.GetColumn(i, "CHECK")==1)
			{
				ds_list1.SetColumn(i, "CHECK", 0);
            }
        }
        ds_list1.fireevent = true;
	    //=========================================//

		gfn_showMsg("CI00003");	
    }
}    



/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_list1.fireevent = false;

	if(strColumnID!="CHECK")
	{
		ds_list1.SetColumn(nRow, "CHECK", 1);
	}

	switch( strColumnID ) {
		case "CONF":
			if(ds_list1.GetColumn(nRow,"CONF") == "Y")
			{
				ds_list1.SetColumn(nRow,"CONF_DATE", gfn_getCurrDate() );							
				//var arr = ["신청","신청"];
				//gfn_showMsg("CW00225",arr);
				break;
			}	
			else 
			{			
				ds_list1.SetColumn(nRow,"CONF_DATE", "");							
				//var arr = ["접수","접수"];
				//gfn_showMsg("CW00225",arr);
				break;
			}				
	}

	ds_list1.fireevent = true;	
}




/********************************************************************************
* 기      능   : 조회 조건에 따른 화면
********************************************************************************/
function cb_state_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(ds_cond.GetColumn(0,"PERIOD") == 'A')
	{
	  grd_list1.Format = "Default";
	}
	else
	{
	  grd_list1.Format = "Default0";
	}
} 

/********************************************************************************
* 기      능   : 그리드 셀 클릭 시 이벤트
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var arg = "";
    arg += " QTNUM="  + quote(ds_list1.GetColumn(nRow, "QTNUM"));    // 견적번호
	arg += " QTSEQ="  + quote(ds_list1.GetColumn(nRow, "QTSEQ"));    // 일련번호
	arg += " VBELN="  + quote(ds_list1.GetColumn(nRow, "ZZPJT_ID")); // 프로젝트번호
	arg += " HOGI="   + quote(ds_list1.GetColumn(nRow, "HOGI"));     // 호기
	arg += " GTYPE="  + quote(ds_list1.GetColumn(nRow, "GTYPE"));    // 기종
	arg += " ZUSE="   + quote(ds_list1.GetColumn(nRow, "ZUSE"));     // 용도
	arg += " TYPE1="  + quote(ds_list1.GetColumn(nRow, "TYPE1"));    // 인승
	arg += " TYPE2="  + quote(ds_list1.GetColumn(nRow, "TYPE2"));    // 속도
	arg += " GSNAM="  + quote(ds_list1.GetColumn(nRow, "GSNAM"));    // 공사명
	arg += " ZKUNNR=" + quote(ds_list1.GetColumn(nRow, "ZKUNNR"));   // 영업담당
	arg += " NAME1="  + quote(ds_list1.GetColumn(nRow, "NAME1"));    // 기술영업담당
	arg += " NAMET="  + quote(ds_list1.GetColumn(nRow, "NAMET"));    // PM
	arg += " QTSER="  + quote(ds_list1.GetColumn(nRow, "QTSER"));    // 견적차수
	arg += " ECWTP="  + quote(ds_list1.GetColumn(nRow, "ECWTP"));    // CWT위치
	arg += " CBRK="   + quote(ds_list1.GetColumn(nRow, "CBRK"));     // 공용브라켓
	arg += " SUVSN="  + quote(ds_list1.GetColumn(nRow, "SUVSN"));    // 실측고유번호
	arg += " SUVDAT=" + quote(ds_list1.GetColumn(nRow, "SUVDAT"));   // 실측일자 
	arg += " SUVNAM=" + quote(ds_list1.GetColumn(nRow, "SUVNAM"));   // 실측자
	arg += " HTSER="  + quote(ds_list1.GetColumn(nRow, "HTSER"));    // 차수
	arg += " CD_R_TYPE='H'";
	if(ds_list1.GetColumn(nRow, "HTSER") > 0) {
	    var pagUrl = "PS11::PS1140002A.xml";
	    gfn_openPopupUrl(pagUrl, true, arg);
	}
}
