function fn_OnLoadCompleted(obj) {

	div_btn.fn_loadUseBtn("900", F_TITLE, "", "");
    fn_resize();
	gfn_initForm();
	fn_init();
}

/********************************************************************************
* 기      능   : 화면초기화
********************************************************************************/
function fn_init() {
	tab_main.TabIndex = decode(CD_R_TYPE, "H", 0,  "M", 1,  "C", 2,  "E", 3);

	gfn_showMsg("");
	var code  = "CD_R_TYPE,ELR_H_MSYN,ELR_H_BTNP,ELR_H_SILLTP,ELR_H_CBRKTP,ELR_H_CWTBG,ELR_H_AUSE"; // 승강로 콤보코드
	    code += ",ELR_M_MBSP,ELR_M_CHSP,ELR_M_SBSP,ELR_M_MBTP";              // 기계실 콤보코드
	    code += ",ELR_E_JAMBTP,ELR_E_HLLC,ELR_E_FSLC,ELR_E_IDTP,ELR_C_G";            // 승장부문 콤보코드
	fn_query_code(code);

	// 조회해온 데이터로 탭헤더 설정
	for(var i = 0; i < ds_CD_R_TYPE.GetRowCount(); i++) {
		var suvprd     = ds_CD_R_TYPE.GetColumn(i, "SUVPRD");
		var suvprdName = ds_CD_R_TYPE.GetColumn(i, "SUVPRDNAME");
		var comp       = decode(suvprd, "H", "tab_hh", "M", "tab_mm", "C", "tab_cc", "E", "tab_ee");
		object("tab_main."+comp).Text = suvprdName;
	}

	ds_cond.ClearData();
	ds_cond.AppendRow();
	ds_cond.SetColumn(0, "QTNUM",  QTNUM);  // 견적번호
	ds_cond.SetColumn(0, "QTSEQ",  QTSEQ);  // 일련번호
	ds_cond.SetColumn(0, "VBELN",  VBELN);  // 프로젝트번호
	ds_cond.SetColumn(0, "HOGI",   HOGI);   // 호기
	ds_cond.SetColumn(0, "GTYPE",  GTYPE);  // 기종
	ds_cond.SetColumn(0, "ZUSE",   ZUSE);   // 용도
	ds_cond.SetColumn(0, "TYPE1",  TYPE1);  // 인승
	ds_cond.SetColumn(0, "TYPE2",  TYPE2);  // 속도
	ds_cond.SetColumn(0, "GSNAM",  GSNAM);  // 공사명
	ds_cond.SetColumn(0, "ZKUNNR", ZKUNNR); // 영업담당
	ds_cond.SetColumn(0, "NAME1",  NAME1);  // 기술영업담당
	ds_cond.SetColumn(0, "NAMET",  NAMET);  // PM
	ds_cond.SetColumn(0, "QTSER",  QTSER);  // 견적차수
	ds_cond.SetColumn(0, "ECWTP",  ECWTP);  // CWT위치
	ds_cond.SetColumn(0, "CBRK",   CBRK);   // 공용브라켓
	ds_cond.SetColumn(0, "SUVSN",  SUVSN);  // 실측고유번호
	ds_cond.SetColumn(0, "SUVDAT", SUVDAT); // 실측일자 
	ds_cond.SetColumn(0, "SUVNAM", SUVNAM); // 실측자
	ds_cond.SetColumn(0, "HTSER",  HTSER);  // 이력차수

    ds_head.ClearData();

	fn_query();
	//trace(ds_rad_prh.saveXML());
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

function fn_resize() {
	window.top    = GetPlatformInfo("WorkArea","top");
	window.left   = GetPlatformInfo("WorkArea","left");
	window.width  = GetPlatformInfo("WorkArea","width");
	window.height = GetPlatformInfo("WorkArea","height");
}

/********************************************************************************
* 기      능   : 탭이 바뀔때 동작을 지정
********************************************************************************/
function tab_main_OnChanged(obj,nOldIndex,nNewindex) {
	// 탭이 변경될때 ID를 전체변수에 저장
	tab_id     = decode(nNewindex, 0, "tab_hh", 1, "tab_mm", 2, "tab_cc", 3, "tab_ee");

	var rowCnt = 0; 
	var sRow   = -1;
	gfn_showMsg("");

	if(tab_id == "tab_hh") {
		ds_cond.SetColumn(0, "DATADIV", 'ELR'); // 데이터구분
		ds_cond.SetColumn(0, "ITEMDIV", 'HH');  // 항목구분
		rowCnt = ds_hh_dummy.GetRowCount();

	}else if(tab_id == "tab_mm") {
		ds_cond.SetColumn(0, "DATADIV", 'ELR'); // 데이터구분
		ds_cond.SetColumn(0, "ITEMDIV", 'MM');  // 항목구분		
		rowCnt = ds_mm_dummy.GetRowCount();

	}else if(tab_id == "tab_cc") {
		ds_cond.SetColumn(0, "DATADIV", 'ELR'); // 데이터구분
		ds_cond.SetColumn(0, "ITEMDIV", 'CF');  // 항목구분
		rowCnt = ds_cc_dummy.GetRowCount();

	}else if(tab_id == "tab_ee") {
		ds_cond.SetColumn(0, "DATADIV", 'ELR'); // 데이터구분
		ds_cond.SetColumn(0, "ITEMDIV", 'EE');  // 항목구분
		rowCnt = ds_ee_dummy.GetRowCount();
	}

	if(rowCnt < 1) {
		if(fn_isNull(ECWTP)) ECWTP = "Y";
		sRow = ds_img_url.SearchRow("tab="+quote(tab_id)+"&&ecwtp="+quote(ECWTP)+"&&cbrk="+quote(CBRK));
		if(sRow > -1) {
			object(ds_img_url.GetColumn(sRow, "img_comp")).ImageID  = ds_img_url.getColumn(sRow, "img_url");
			object(ds_img_url.GetColumn(sRow, "img_comp")).FillType = ds_img_url.getColumn(sRow, "fill_type");
		}

		if(ECWTP == "R") {
			tab_main.tab_hh.div_bottom.grd_hh_02.format = "default";
			tab_main.tab_hh.div_bottom.grd_hh_03.format = "default";

		}else if(ECWTP != "R" && CBRK == "Y") {
			tab_main.tab_hh.div_bottom.grd_hh_02.format = "cbrk";
			tab_main.tab_hh.div_bottom.grd_hh_03.format = "ecwtp";

		}else if(ECWTP != "R" && CBRK == "N") {
			tab_main.tab_hh.div_bottom.grd_hh_02.format = "ecwtp";
			tab_main.tab_hh.div_bottom.grd_hh_03.format = "ecwtp";
		}

		fn_query_tab();
	}
}

/********************************************************************************
* 기      능   : 헤더조회
********************************************************************************/
function fn_query() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps11:PS1140002A_S1"); // 헤더 조회

	 tit_callService(""
                    ,""
                    ,"ds_cond=ds_cond"
                    ,"ds_head=ds_head"
                    ,""
                    ,"fn_afterQuery"
                    ,""
                    ,""
                    ,true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	ds_hh_dummy.ClearData();
	ds_mm_dummy.ClearData();
	ds_cc_dummy.ClearData();
	ds_ee_dummy.ClearData();

	// 헤더 조회 후 활성화된 탭의 데이터를 조회
	tab_main_OnChanged(tab_main, -1, tab_main.TabIndex);
	var hogi = ds_head.GetColumn(0, "HOGI");
	divTop.edt_HOGI.Text = decode(hogi, "", ds_head.GetColumn(0, "QTSEQ"), hogi);
}

/********************************************************************************
* 기      능   : 탭 조회 
********************************************************************************/
function fn_query_tab() {
	ds_dummy.ClearData();
	tit_clearActionInfo();
	if(fn_isNull(ds_cond.GetColumn(0, "DATADIV"))) return;
	tit_addSearchActionInfo("ps11:PS1140002A_S2"); // 평면도 실측 조회
	var out_data_set;

	if(tab_id == "tab_hh") {
		out_data_set = "ds_hh_dummy=ds_temp";

	}else if(tab_id == "tab_mm") {
		out_data_set = "ds_mm_dummy=ds_temp";

	}else if(tab_id == "tab_cc") {
		out_data_set = "ds_cc_dummy=ds_temp";

	}else if(tab_id == "tab_ee") {
		out_data_set = "ds_ee_dummy=ds_temp";
	}

	if(tab_id == "tab_ee") {
		tit_addSearchActionInfo("ps11:PS1140005A_S1"); // 승장부분 실측 조회
		tit_addSearchActionInfo("ps11:PS1140005A_S2"); // JAMB 실측 조회
		out_data_set += " ds_ee_hpb=ds_hpb ds_ee_jamb=ds_jamb";
	}

	 tit_callService(""
                    ,""
                    ,"ds_cond=ds_cond"
                    ,out_data_set
                    ,""
                    ,"fn_after_query_tab"
                    ,""
                    ,""
                    ,true);
}

/********************************************************************************
* 기      능   : 탭 조회 Callback  
********************************************************************************/
function fn_after_query_tab(errCode, errMsg) {
    /*
	if(tab_id == "tab_hh") {
	    trace("ds_hh_dummy:"+ds_hh_dummy.SaveXML());
	}else if(tab_id == "tab_mm") {
	    trace("ds_mm_dummy:"+ds_mm_dummy.SaveXML());
	}else if(tab_id == "tab_cc") {
	    trace("ds_cc_dummy:"+ds_cc_dummy.SaveXML());
	}else if(tab_id == "tab_ee") {
	    trace("ds_ee_dummy:"+ds_ee_dummy.SaveXML());
	}
    */

	if(tab_id == "tab_hh") {
		column_split(tab_id);

		if(fn_isNull(ds_hh_03.GetColumn(0, "ELR_H_SILlRU"))) ds_hh_03.SetColumn(0, "ELR_H_SILlRU", "0");
		if(fn_isNull(ds_hh_03.GetColumn(0, "ELR_H_SILLRM"))) ds_hh_03.SetColumn(0, "ELR_H_SILLRM", "0");

	}else if(tab_id == "tab_mm") {
		column_split(tab_id);
		ds_filter_copy("==", "||", tab_id);

	}else if(tab_id == "tab_cc") {
		column_split(tab_id);
		ds_filter_copy("==", "||", tab_id);
		tab_main.tab_cc.div_top.grd_cs.VScrollPos = 24;

	}else if(tab_id == "tab_ee") {
		column_split(tab_id);
	}
}

// 데이터셋 필터링 후 복사
function ds_filter_copy(sign, symbol, tab) {
	var col     = "SUVPRH";
	if(tab_id == "tab_cc") col = "ITEMDIV";
	var cnt     = ds_column_filter.GetRowCount();

	for(var x = 0; x < cnt; x++) {
		var temp_ds   = object(ds_column_filter.GetColumn(x, "temp_ds"));
		var order_tab = ds_column_filter.GetColumn(x, "tab");

		if(tab == order_tab) {
			var ds = object(ds_column_filter.GetColumn(x, "dataSet"));
			var arr = split(ds_column_filter.GetColumn(x, "column"), ",");
			var i = 0;
			var filter = "";
			while(i < arr.length()) {
				if(i == 0) {
					filter = col+sign+quote(arr[i]);
				}else {
					filter += symbol+col+sign+quote(arr[i]);
				}
				i++;
			}

			temp_ds.Filter(filter);
			ds.CopyF(temp_ds);
			temp_ds.UnFilter();
		}
	}
}

// 열을 횡으로 변환
function column_split(tab) {
	var cnt = ds_column_split.GetRowCount();
	for(var x = 0; x < cnt; x++) {
		var temp_ds   = object(ds_column_split.GetColumn(x, "temp_ds"));
		var order_tab = ds_column_split.GetColumn(x, "tab");
		var ds        = object(ds_column_split.GetColumn(x, "dataSet"));
		var row       = 0;

		if(tab == order_tab) {
			if(tab_id != "tab_ee") {
				ds.clearData();
				row  = ds.AppendRow();
			}

			var arr = split(ds_column_split.GetColumn(x, "column"), ",");
			var grd = object(ds_column_split.GetColumn(x, "grid"));
			var i = 0;
			while(i < arr.length()) {
				var fRow = temp_ds.FindRow("SUVPRH", arr[i]);
				if(fRow > -1) {
					var data    = temp_ds.GetColumn(fRow, "SUVPRD");
					var req     = temp_ds.GetColumn(fRow, "NECYSNO");
					//var if_req  = temp_ds.GetColumn(fRow, "IFNECYSNO");
					if(tab_id != "tab_ee") ds.SetColumn(row, arr[i], data);

					for(var y = 0; y < grd.GetColCount(); y++) {
						if(arr[i] == grd.GetCellProp("body" ,y ,"ColId")) {
							if(req == "Y")    grd.SetCellProp("body" ,y ,"BkColor" ,"User27"); // antiquewhite
							//if(if_req == "Y") grd.SetCellProp("body",y,"BkColor","silver");
						}
					}
				}
				i++;
			}
			//ds.OnColumnChanged = "ds_horizontal_OnColumnChanged";
		}
	}
}

