/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	var cnt = 0;
	for(var i = 0; i < ds_moded_dataset.GetRowCount(); i++) {
		var ds = object(ds_moded_dataset.GetColumn(i, "dataset"));
		//trace(ds.id+", "+ds.GetUpdate());
		cnt += ds.GetUpdate();
	}	

	cnt += ds_head.GetUpdate();
	cnt += ds_ee_hpb.GetUpdate();
	cnt += ds_ee_jamb.GetUpdate();

	if(cnt > 0) {
		var msg = "변경 사항이 있습니다. \n"+
		          "이대로 닫으면, 변경사항은 저장 되지 않습니다.\n"+
		          "저장/임시저장을 눌러 저장 할 수 있습니다.\n"+
		          "창을 닫으시겠습니까?";
		if(gfn_openMsgWin(msg ,"I" ,"C")) this.close(saveYn);
	}else {
		this.close(saveYn);
	}
}

// 값이 변경됐을 경우 표시
function ds_dummy_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if(strColumnID != "FLAG") {
		var suvprd   = trim(obj.getColumn(nRow, "SUVPRD"));
		var o_suvprd = trim(obj.getColumn(nRow, "O_SUVPRD"));
		if(suvprd <> o_suvprd) obj.SetColumn(nRow, "FLAG", "U");
		if(suvprd == o_suvprd) obj.SetColumn(nRow, "FLAG", "");
	}
}

// 승장부문 값이 변경됐을 경우 표시
function ds_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {

	if(strColumnID != "FLAG") {
		obj.OnColumnChanged = "";
		gfn_showMsg("");
		if(obj.id == "ds_ee_hpb") {
			if(strColumnID == "ELR_E_IDTP") {
				gfn_showMsg("표시기종류 선택에 따라 소방스위치 위치, 홀랜턴위치는 선택 가능 합니다.");

				if(varOldValue == "HLT") obj.setColumn(nRow, "ELR_E_HLLC", "");
				if(varOldValue == "FSW") obj.setColumn(nRow, "ELR_E_FSLC", "");
			}
		}
		if(obj.GetColumn(nRow, "FLAG") == "" || obj.GetColumn(nRow, "FLAG") == "U") {
			obj.SetColumn(nRow, "FLAG", "");
			if(obj.getRowType(nRow) == "update") obj.SetColumn(nRow, "FLAG", "U");
		}
		obj.OnColumnChanged = "ds_OnColumnChanged";
	}
}

/********************************************************************************
* 기      능   : 공통코드 조회
********************************************************************************/
function fn_query_code(code) {
    var arr = split(code, ",");
    for(var i = 0; i < arr.length(); i++) {
        ds_cond.ClearData();
        ds_cond.AppendRow();
        ds_cond.SetColumn(0, "SUVPRH", arr[i]);

        tit_clearActionInfo();
        tit_addSearchActionInfo("ps11:PS1120002A_S3"); // 헤더 조회

        tit_callService(""
                       ,""
                       ,"ds_cond=ds_cond"
                       ,"ds_dummy=ds_dummy"
                       ,""
                       ,"fn_afterQuery_code"
                       ,""
                       ,""
                       ,true);
    }
}

/********************************************************************************
* 기      능   : 공통코드 조회 뒤 Callback  
********************************************************************************/
function fn_afterQuery_code(errCode, errMsg) {
	var ds = object("ds_"+ds_cond.GetColumn(0, "SUVPRH"));
	ds.copy(ds_dummy);

	if(pos("ds_ELR_M_MBSP,ds_ELR_M_CHSP,ds_ELR_M_SBSP", ds.id) > -1) {
		ds.InsertRow(0);
	}	
}

// 데이터가 변경될 경우 즉시 위쪽데이터셋에 반영한다.
function ds_horizontal_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {

	if(fn_isNull(varNewValue) && fn_isNull(varOldValue)) return;

	var main_ds = object(decode(tab_id ,"tab_hh" ,"ds_hh_dummy"
	                                   ,"tab_mm" ,"ds_mm_dummy"
	                                   ,"tab_cc" ,"ds_cc_dummy"));

    // 3상4선, 3상3선 선택시 반대값 설정
    if(obj.id == "ds_mm_01" && strColumnID == "ELR_M_PWTV") {
		ds_mm_02.SetColumn(0, "ELR_M_PWTL", decode(varNewValue, "1", "0", "1"));

    }else if(obj.id == "ds_mm_02" && strColumnID == "ELR_M_PWTL") {
		ds_mm_01.SetColumn(0, "ELR_M_PWTV", decode(varNewValue, "1", "0", "1"));
    }

	var fRow = main_ds.FindRow("SUVPRH", strColumnID);
	main_ds.SetColumn(fRow ,"SUVPRD" ,trim(varNewValue));

	    fRow = ds_column_split.FindRow("dataSet", obj.id);
	var arr  = split(ds_column_split.GetColumn(fRow, "column"), ",");
	var i    = 0;
	var chg  = "N";
	while(i < arr.length()) {
			fRow  = main_ds.FindRow("SUVPRH", arr[i]);
		var data  = trim(main_ds.GetColumn(fRow, "SUVPRD"));
		var oData = trim(main_ds.GetColumn(fRow, "O_SUVPRD"));

		if(data <> oData) {
			chg = "Y";
		}
		i++;
	}

	if(chg == "Y") {
		obj.SetColumn(nRow, "FLAG", "U");
	}else {
		obj.SetColumn(nRow, "FLAG", "");
	}

	var sRow = ds_checkbox_control.SearchRow("tab="+quote(tab_id)+"&&column="+quote(strColumnID)+"&&true_value="+quote(varNewValue));
	if(sRow == -1) return; // ds_checkbox_control 데이터셋에 데이터가 없다면 리턴.

	var checkbox_t  = ds_checkbox_control.GetColumn(sRow ,"checkbox_t");
	var checkbox_o  = ds_checkbox_control.GetColumn(sRow ,"checkbox_o");
	var address     = ds_checkbox_control.GetColumn(sRow ,"address");
	var true_value  = ds_checkbox_control.GetColumn(sRow ,"true_value");
	var false_value = ds_checkbox_control.GetColumn(sRow ,"false_value");

	object(address+checkbox_t).value = 1;
	object(address+checkbox_o).value = 0;
}

// 체크박스 선택시 데이터셋에 반영하고 상대편 체크박스 처리
function fn_Checkbox_OnClick(obj, strValue) {

	var sRow = ds_checkbox_control.SearchRow("tab="+quote(tab_id)+"&&checkbox_t="+quote(obj.id));

	var checkbox_o  = ds_checkbox_control.GetColumn(sRow, "checkbox_o");
	var true_value  = ds_checkbox_control.GetColumn(sRow, "true_value");
	var false_value = ds_checkbox_control.GetColumn(sRow, "false_value");
	var ds          = object(ds_checkbox_control.GetColumn(sRow, "dataSet"));
	var column      = ds_checkbox_control.GetColumn(sRow, "column");
	var address     = ds_checkbox_control.GetColumn(sRow, "address");

	var value = decode(strValue, "1", true_value, false_value);

	ds.SetColumn(ds.getCurrow(), column, value);
	object(address+checkbox_o).value = decode(strValue, "1", "0", "1");
}

/********************************************************************************
* 기      능   : 저장버튼 누르면 실행
********************************************************************************/
function fn_save() {
	// 조회되지 않은 탭이 있을경우, 전체 조회
	if(ds_hh_dummy.GetRowCount() < 1) tab_main_OnChanged(tab_main, -1, 0);
	if(ds_mm_dummy.GetRowCount() < 1) tab_main_OnChanged(tab_main, -1, 1);
	if(ds_cc_dummy.GetRowCount() < 1) tab_main_OnChanged(tab_main, -1, 2);
	if(ds_ee_dummy.GetRowCount() < 1) tab_main_OnChanged(tab_main, -1, 3);

	tab_id = decode(tab_Main.TabIndex, 0, "tab_hh", 1, "tab_mm", 2, "tab_cc", 3, "tab_ee");

	// 전체조회 이전에 처리될경우 일시 멈춤
	if(ds_hh_dummy.GetRowCount() < 1 || ds_mm_dummy.GetRowCount() < 1 || 
	   ds_cc_dummy.GetRowCount() < 1 || ds_ee_dummy.GetRowCount() < 1) {
		gfn_openMsgWin("데이터 검증 중 입니다. 저장버튼을 다시 눌러주세요.","I");		
		return;
	}

	// 필수값 체크
	if(!fn_required_check()) return;

	// 입력된 값 계산식으로 체크
	if(!fn_input_formula()) return;

	ds_head.SetColumn(0, "HRST",  "Y"); // 승강로등록상태 Y:작성완료
	ds_head.SetColumn(0, "MRST",  "Y");
	ds_head.SetColumn(0, "CRST",  "Y");
	ds_head.SetColumn(0, "ERST",  "Y");

	if(gfn_showConfirm("CI10003")) fn_preSave(); // 저장하시겠습니까?	
}

/********************************************************************************
* 기      능   : 임시저장버튼 누르면 실행
********************************************************************************/
function fn_preSave() {
	saveYn = "Y";	
	var SUVSN = ds_head.GetColumn(0, "SUVSN");
	if(fn_isNull(SUVSN)) {
		saveStat = "P"; // 조회 후 메세지 나오지 않게
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps11:PS1120002A_S4"); // 실측고유번호 조회

		 tit_callService(""
						,""
						,"ds_cond=ds_cond"
						,"ds_dummy=ds_dummy"
						,""
						,"fn_afterSave"
						,""
						,""
						,true);

		ds_head.SetColumn(0, "SUVSN", ds_Dummy.GetColumn(0, "SUVSN"));
		ds_cond.SetColumn(0, "SUVSN", ds_Dummy.GetColumn(0, "SUVSN"));
	}

	saveStat = "S";
	// 신규, 수정된 실측값만 복사해 저장
	ds_list.ClearData();

	// 수정된 데이터셋을 저장 데이터셋으로 복사.
	for(var i = 0; i < ds_moded_dataset.GetRowCount(); i++) {
		var ds      = object(ds_moded_dataset.GetColumn(i, "dataSet"));
		var suvType = ds_moded_dataset.GetColumn(i, "suvType");

		fn_moded_dataset_copy(ds, suvType);
	}

	// 승장부문 데이터셋 처리
	ds_ee_append.ClearData();
	ds_ee_delete.ClearData();
	ds_ee_update.ClearData();

	// 승장부문 등록된 데이터만 복사
	var cnt = ds_column_split.GetRowCount();
	for(var i = 0; i < cnt; i++) {
		var wds = object(ds_column_split.GetColumn(i, "dataSet"));
		var id  = ds_column_split.GetColumn(i, "tab");
		if(id == "tab_ee") {
			var column = split(ds_column_split.GetColumn(i, "column"), ",");
			fn_data_copy(wds ,column);
		}
	}

	if(ds_list.GetRowCount() > 0) {
		// CWT위치 추가
		var nRow = ds_list.AppendRow();
		ds_list.CopyRow(nRow, ds_list, 0);
		ds_list.SetColumn(nRow, "INTYPE", "COMBO");
		ds_list.SetColumn(nRow, "SUVPRH", "ELR_B_ECWTP");
		ds_list.SetColumn(nRow, "SUVPRD", ds_head.GetColumn(0, "ECWTP"));

		// 공용브라켓 추가
		nRow = ds_list.AppendRow();
		ds_list.CopyRow(nRow, ds_list, 0);
		ds_list.SetColumn(nRow, "INTYPE", "COMBO");
		ds_list.SetColumn(nRow, "SUVPRH", "ELR_B_CBRK");
		ds_list.SetColumn(nRow, "SUVPRD", ds_head.GetColumn(0, "CBRK"));
	}

	for(var i = 0; i < ds_list.GetRowCount(); i++) {
		ds_list.SetColumn(i, "SUVSN", ds_head.GetColumn(0, "SUVSN"));
		var inType = ds_list.GetColumn(i, "INTYPE");
		var suvPrd = ds_list.GetColumn(i, "SUVPRD");

		if(inType == "CHECK") {
			ds_list.SetColumn(i, "SUVPRD",  decode(suvPrd, "1", "Y", "0", "N", suvPrd));
			//ds_list.SetColumn(i, "MODYPRD", decode(suvPrd, "1", "Y", "0", "N"));
		}//else {
			//ds_list.SetColumn(i, "MODYPRD", suvPrd); // 실측사양값 = 보정사양값
		//}
	}

	tit_clearActionInfo();
	var ZZPJT_ID = ds_head.GetColumn(0, "ZZPJT_ID");
	// 수주번호 유무에 따른 분기
	if(length(trim(ZZPJT_ID)) < 1) {
		tit_addSingleActionInfo("ps11:PS1120002A_I1"); // 견적실측 등록
	}else {
		tit_addSingleActionInfo("ps11:PS1120002A_I2"); // 수주실측 등록
	}

	var input_dataset = "ds_head=ds_head";
	// 수정된 실측치가 없으면 호출하지 않음
	if(ds_list.GetRowCount() > 0) {
		tit_addMultiActionInfo("ps11:PS1120002A_I3");
		input_dataset += " ds_list=ds_list";
	}

	// 삭제 실측치가 없으면 호출하지 않음
	if(ds_ee_delete.GetRowCount() > 0) {
		tit_addMultiActionInfo("ps11:PS1120005A_D1");
		input_dataset += " ds_ee_delete=ds_ee_delete";
	}

	// 신규 실측치가 없으면 호출하지 않음
	if(ds_ee_append.GetRowCount() > 0) {
		tit_addMultiActionInfo("ps11:PS1120005A_I1");
		input_dataset += " ds_ee_append=ds_ee_append";
	}

	// 수정된 실측치가 없으면 호출하지 않음
	if(ds_ee_update.GetRowCount() > 0) {
		tit_addMultiActionInfo("ps11:PS1120005A_U1");
		input_dataset += " ds_ee_update=ds_ee_update";
	}

	tit_callService(""
				   ,""
				   ,input_dataset
				   ,""
				   ,""
				   ,"fn_afterSave"
				   ,true);
}

// 수정된 데이터를 가진 데이터셋만 복사한다.
function fn_moded_dataset_copy(ds, suvType) {
	for(var i = 0; i < ds.GetRowCount(); i++) {
		if(ds.GetColumn(i, "FLAG") == "U") {
			var nRow = ds_list.AppendRow();
			ds_list.CopyRow(nRow, ds, i);
			ds_list.SetColumn(nRow, "SUVTYPE", suvType);
		}
	}
}

// 승장부문 데이터 복사
function fn_data_copy(ds ,arr) {
	var x = 0;
	while(x < 3) {
		var job = decode(x, 0, "I", 1, "U", 2, "D");
		var wds = object(decode(x, 0, "ds_ee_append", 1, "ds_ee_update", 2, "ds_ee_delete"));
		for(var i = 0; i < ds.GetRowCount(); i++) {
			if(ds.GetColumn(i, "FLAG") == job) {
				var y = 0;
				while(y < arr.length()) {
					if(arr[y] != "APPLYFLOOR") {
						var nRow = wds.AppendRow();
						wds.SetColumn(nRow ,"SUVSN"      ,ds_head.GetColumn(0, "SUVSN"));
						wds.SetColumn(nRow ,"INORDER"    ,ds.GetColumn(i, "INORDER"));
						wds.SetColumn(nRow ,"APPLYFLOOR" ,ds.GetColumn(i ,"APPLYFLOOR"));
						wds.SetColumn(nRow ,"SUVPRH"     ,arr[y]);
						wds.SetColumn(nRow ,"SUVPRD"     ,ds.GetColumn(i ,arr[y]));
					}
					y++;
				}
			}
		}
		x++;
	}
}

// 저장 전 실측치 필수체크
function fn_required_check() {

	fn_required_change(); // 저장 전 조건필수 변경

	var wds = ds_column_split;
	for(var i = 0; i < wds.GetRowCount(); i++) {
		if(!required_check02(i)) return false;
	}

	wds = ds_column_filter;
	for(var i = 0; i < wds.GetRowCount(); i++) {
		var grd       = wds.GetColumn(i, "grid");
		var ds        = wds.GetColumn(i, "dataSet");
		var tab_index = wds.GetColumn(i, "tab_index");

		if(!required_check01(object(ds), grd, tab_index)) return false;
	}

	if(ds_ee_hpb.CaseCount("FLAG!='D'") < 1) {
		gfn_openMsgWin("표시기(HPB) 정보를 등록 해 주세요.","W");
		tab_main.TabIndex = 3;
		return false;
	}

	if(ds_ee_jamb.CaseCount("FLAG!='D'") < 1) {
		gfn_openMsgWin("JAMB 정보를 입력 해 주세요.","W");
		tab_main.TabIndex = 3;
		return false;
	}

	return true;
}

// 입력한 값 계산식으로 체크
function fn_input_formula() {
	// 실측A+ Car Rail 가로 + 실측치BG/2 -JJ/2	실측치 LL값과 비교하여 50mm 이하 차이 날 것 ( 불만족 시 경고 )
    var H_A     = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_A"));	
	var H_CARBG = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_CARBG"));
	var H_JJ    = parseInt(ds_hh_01.GetColumn(0 ,"ELR_H_JJ"));
	var H_LL    = parseInt(ds_hh_01.GetColumn(0 ,"ELR_H_LL"));

	var H_CARRAIL = ds_hh_03.GetColumn(0 ,"ELR_H_CARRAIL");
	var H_CAR_A   = parseInt(ds_car_rail.GetColumn(ds_car_rail.FindRow("rail", H_CARRAIL)  ,"a"));	

	// 실측B+ Car Rail 가로 + 실측치BG/2 -JJ/2	실측치 RL값과 비교하여 50mm 이하 차이 날 것 ( 불만족 시 경고 )
	var H_B  = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_B"));
	var H_RL = parseInt(ds_hh_01.GetColumn(0 ,"ELR_H_RL"));

	//(실측치CA-실측치JJ) / 2 와 일치 여부 검토 ( 오차 10mm ) 
	var H_CA = parseInt(ds_hh_01.GetColumn(0 ,"ELR_H_CA"));
	var H_W1 = parseInt(ds_hh_01.GetColumn(0 ,"ELR_H_W1"));

	// 검토용. H=J 확인 할 것. ( 불만족 시 경고 )
	var H_H = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_H"));
	var H_J = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_J"));

	// X 계산식 적용	실측A+실측B+(Car Rail 가로 x2) + 실측치 BG	실측치 X와 비교차여 오차200mm 이하 검토. ( 불만족 시 경고 )
	var H_X = parseInt(ds_hh_03.GetColumn(0 ,"ELR_H_X"));

	// Y 계산식 적용	실측C+실측E+실측PP+실측F	실측치 Y와 비교차여 오차200mm 이하 검토. ( 불만족 시 경고 )	
	var H_C  = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_C"));
	var H_E  = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_E"));
	var H_PP = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_PP"));
	var H_F  = parseInt(ds_hh_02.GetColumn(0 ,"ELR_H_F"));
	var H_Y  = parseInt(ds_hh_03.GetColumn(0 ,"ELR_H_Y"));

	for(var i = 0; i < ds_formula.GetRowCount(); i ++) {
		var result    = eval(ds_formula.GetColumn(i, "formula"));
		var tab_index = ds_formula.GetColumn(i, "tab_index");
		//trace("result >> "+result);
		if(result > parseInt(ds_formula.GetColumn(i, "range"))) {
			var msg = ds_formula.GetColumn(i, "message");
			    msg = replace(msg, "$1", result);
			gfn_openMsgWin(msg, "W");
			tab_main.TabIndex = tab_index;
			return false;
		}
	}
	/*
	for(var i = 0; i < ds_formula_2.GetRowCount(); i ++) {
		var sRow = parent.ds_rad_prh.SearchRowNF(ds_formula_2.GetColumn(i, "search"));
		if(sRow > -1) {
			var PRHNAME   = ds_rad_prh.getcolumn(sRow, "PRHNAME");
			var PRD       = ds_rad_prh.getcolumn(sRow, "PRD");
			var tab_index = ds_formula_2.GetColumn(i, "tab_index");

			if(fn_isNull(PRD)) {
				gfn_openMsgWin("영업사양에 "+PRHNAME+"값이 입력되지 않았습니다.", "W");
				tab_main.TabIndex = tab_index;
				return false;
			}
			var col = ds_formula_2.GetColumn(i, "col");
			if(eval("H_"+col) != PRD) {
				gfn_openMsgWin(col+"값이 영업사양\n"+quote(PRHNAME)+"\n값 [ "+PRD+" ]과 다릅니다.\n"+
				               "실측입력값을 확인 해 주십시오.", "W");
				tab_main.TabIndex = tab_index;
				return false;
			}
		}
	}*/

	return true;
}

// 저장 전 조건필수 변경
function fn_required_change() {

	for(var i = 0; i < ds_requerd_change.GetRowCount(); i++) {
		var ds     = object(ds_requerd_change.GetColumn(i, "dataset"));
		var col    = ds_requerd_change.GetColumn(i, "column");
		var val    = ds_requerd_change.GetColumn(i, "value");
		var r_col  = ds_requerd_change.GetColumn(i, "requred_column");

		var fRow   = ds.FindRow("SUVPRH", col);
		var suvprd = ds.GetColumn(fRow, "SUVPRD");
		    fRow   = ds.FindRow("SUVPRH", r_col);
		var r_val  = ds.GetColumn(fRow, "SUVPRD");

		if(!fn_isNull(r_val)) continue; // 저장 시점에 타겟값이 있다면 필수여부 변경안함.

		if(pos(suvprd, val) > -1 || pos(val, suvprd) > -1) {
			ds.SetColumn(fRow, "NECYSNO", "Y");
		}else {
			ds.SetColumn(fRow, "NECYSNO", "N");
		}
	}
}

// 그리드 필수체크1
function required_check01(ds, grd, tab_index) {
	var i = 0;
	var sGrd = object(grd);

	while(i < ds.GetRowCount()) {
		var required = ds.GetColumn(i, "NECYSNO");
		var name     = ds.GetColumn(i, "SUVPRHNAME");
		var value    = ds.GetColumn(i, "SUVPRD");

		if(required == "Y" && fn_isNull(value)) {
			gfn_openMsgWin(name+" 는 필수값 입니다.","W");
			tab_main.TabIndex = tab_index;
			ds.row = i;
			sGrd.SetCellPos(2);
			sGrd.SetFocus();
			return false;
		}
		i++;
	}

	return true;
}

// 그리드 필수체크2
function required_check02(index) {
	var required;
	var name;
	var value;
	var suvprh;
	var grd       = object(ds_column_split.getcolumn(index, "grid"));
	var ds        = object(ds_column_split.getcolumn(index, "dataSet"));
    var temp_ds   = object(ds_column_split.getcolumn(index, "temp_ds"));
    var tab_index = ds_column_split.getcolumn(index, "tab_index");
    var arr       = split(ds_column_split.getcolumn(index, "column"), ",");

	for(var x = 0; x < ds.getRowCount(); x++) {
		var i = 0;
		while(i < arr.length()) {
			var fRow = temp_ds.FindRow("SUVPRH", arr[i]);		
			required = temp_ds.GetColumn(fRow, "NECYSNO");
			name     = temp_ds.GetColumn(fRow, "SUVPRHNAME");
			suvprh   = temp_ds.GetColumn(fRow, "SUVPRH");
			value    = temp_ds.GetColumn(fRow, "SUVPRD");

			if(tab_index == 3) {
				value = ds.GetColumn(x ,arr[i]);
				// 승장표시기 종류에 따른 필수조건 변경
				var ELR_E_IDTP = ds.Getcolumn(x, "ELR_E_IDTP");
				if(suvprh == "ELR_E_HLLC" && ELR_E_IDTP == "HLT") { // 홀랜턴 필수
					required = "Y";
				}else if(suvprh == "ELR_E_FSLC" && ELR_E_IDTP == "FSW") { // 소방스위치 필수
					required = "Y";
				}
			}

			if(required == "Y" && fn_isNull(value)) {
				gfn_openMsgWin(name+" 는 필수값 입니다.","W");
				ds.row = x;
				tab_main.TabIndex = tab_index;

				for(var y = 0; y < grd.GetColCount(); y++) {
					var colid = grd.GetCellProp('body', y, 'colid');

					if(colid == suvprh) {
						grd.SetCellPos(y);
					}
				}
				grd.SetFocus();
				return false;
			}
			i++;
		}
	}
	return true;
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {

	if(saveStat == "P") return;

	if(errCode != "0") {
		gfn_showAlert("CE00001");

	}else {
		gfn_showAlert("CI00003");
		fn_query();
	}
}

// 그리드 버튼클릭 시
function fn_grd_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {
	var ds       = object(obj.bindDataSet);
	var sel      = ds.getColumn(nRow, "SUVPRH");
	var selValue = ds.getColumn(nRow, "SUVPRD");
	var selTitle = ds.getColumn(nRow, "SUVPRHNAME");

	// 승강로탭
	if(tab_id == "tab_hh") {
		sel = tab_main.tab_hh.div_bottom.grd_hh_03.GetCellProp('body', nCell, 'colid');
		selValue = ds.getColumn(ds.getcurrow(), sel);
		selTitle = tab_main.tab_hh.div_bottom.grd_hh_03.GetCellProp('head', nCell, 'text');

		var arg = "pr_SUVPRH="+quote(sel)+" pr_SUVPRHNAME="+quote(selTitle)+" pr_SUVPRD="+quote(selValue);	
		var rtn = gfn_openPopupUrl("PS11::PS1120002F.xml", true, arg);

		if(!fn_isNull(rtn)) {
			ds.SetColumn(ds.getcurrow(), sel, rtn);
		}

	// 기계실탭
	}else if(tab_id == "tab_mm") {
		if(obj.id == "grd_mm_04") {
			sel      = tab_main.tab_mm.div_bottom.grd_mm_04.GetCellProp('body', nCell, 'colid');
			selValue = ds.getColumn(ds.getcurrow(), sel);
			selTitle = tab_main.tab_mm.div_bottom.grd_mm_04.GetCellProp('head', nCell, 'text');			
		}

		var arg = "pr_SUVPRH="+quote(sel)+" pr_SUVPRHNAME="+quote(selTitle)+" pr_SUVPRD="+quote(selValue);

		var url = "";
		if(sel == "ELR_M_DOORP") url = "PS11::PS1120003D.xml";  // 기계실위치
		if(sel == "ELR_M_DPBP")  url = "PS11::PS1120003E.xml";  // 분전반위치

		if(sel == "ELR_M_CPX" || sel == "ELR_M_CPY") {          // 제어반위치XY
			var fRow     = ds.findRow("SUVPRH", "ELR_M_CPX");
			var cpxValue = ds.getColumn(fRow, "SUVPRD");
			fRow         = ds.findRow("SUVPRH", "ELR_M_CPY");
			var cpyValue = ds.getColumn(fRow, "SUVPRD");
			arg = "pr_SUVPRH="+quote(sel)+" pr_SUVPRHNAME=제어판위치, pr_ELR_M_CPX="+quote(cpxValue)+", pr_ELR_M_CPY="+quote(cpyValue);
			url = "PS11::PS1120003F.xml";  
		}
		if(sel == "ELR_M_WHP")  url = "PS11::PS1120003G.xml";  // 배선구멍위치
		if(sel == "ELR_M_CGP")  url = "PS11::PS1120003H.xml";  // 조속기위치
		if(sel == "ELR_M_MBCB") url = "PS11::PS1120003I.xml";  // 기계대빔구성
		if(sel == "ELR_M_DHP")  url = "PS11::PS1120003J.xml";  // 기계실 출입구 문 손잡이 위치
		if(sel == "ELR_M_DHD")  url = "PS11::PS1120003K.xml";  // 기계실 출입구 문 손잡이 방향

		var rtn = gfn_openPopupUrl(url, true, arg);

		if(!fn_isNull(rtn)) {
			if(sel == "ELR_M_CPX" || sel == "ELR_M_CPY") {
				var arr  = split(rtn, ",");
				var fRow = ds.findRow("SUVPRH", "ELR_M_CPX");
				ds.setColumn(fRow, "SUVPRD", arr[0]);

				fRow = ds.findRow("SUVPRH", "ELR_M_CPY");
				ds.setColumn(fRow, "SUVPRD", arr[1]);

			}else if(obj.id == "grd_mm_04") {
				ds.SetColumn(nRow, "ELR_M_MBCB", rtn);
			}else {
				ds.SetColumn(nRow, "SUVPRD", rtn);
			}	
		}

	// CWT상세 및 층고탭
	}else if(tab_id == "tab_cc") {
		if(obj.id == "grd_cf") {
			sel      = tab_main.tab_cc.div_bottom.grd_cf.GetCellProp('body', nCell, 'colid');
			selValue = ds.getColumn(ds.getcurrow(), sel);
			selTitle = "기존 CWT 세가지 형상";

		}else if(obj.id == "grd_cb") {
			sel = tab_main.tab_cc.div_bottom.grd_cb.GetCellProp('body', nCell, 'colid');
			selValue = ds.getColumn(ds.getcurrow(), sel);
			selTitle = tab_main.tab_cc.div_bottom.grd_cb.GetCellProp('head', nCell, 'text');
		}

		var arg = "pr_SUVPRH="+quote(sel)+" pr_SUVPRHNAME="+quote(selTitle)+" pr_SUVPRD="+quote(selValue);

		var url = "";
		if(sel == "ELR_C_CWSTP") url = "PS11::PS1120004B.xml";  // 기존 CWT 세가지 형상
		if(sel == "ELR_C_VSP")   url = "PS11::PS1120004C.xml";  // 볼트규격

		var rtn = gfn_openPopupUrl(url, true, arg);

		if(!fn_isNull(rtn)) {
			if(obj.id == "grd_cf") {
				ds.SetColumn(0, "ELR_C_CWSTP", rtn);
			}else if(obj.id == "grd_cb") {
				ds.SetColumn(nRow, "ELR_C_VSP", rtn);
			}
		}

	}else if(tab_id == "tab_ee") {		
	}
}

// +,- 버튼클릭 시
function fn_grd_btn_click(obj) {
	return;
	var nRow = 0;

	if(obj.id == "btn_hpb_plus") {
		var inorder = fn_get_inorder(ds_ee_hpb);
		nRow = ds_ee_hpb.AppendRow();
		ds_ee_hpb.SetColumn(nRow ,"FLAG"    ,"I");
		ds_ee_hpb.SetColumn(nRow ,"INORDER" ,inorder);


	}else if(obj.id == "btn_hpb_minus") {
		nRow = ds_ee_hpb.GetCurrow();
		if(ds_ee_hpb.GetRowType(nRow) != "insert") {
			ds_ee_hpb.SetColumn(nRow, "FLAG", "D");
		}else {
			ds_ee_hpb.DeleteRow(nRow);
		}

	}else if(obj.id == "btn_jamb_plus") {
		var inorder = fn_get_inorder(ds_ee_jamb);
		nRow = ds_ee_jamb.AppendRow();
		ds_ee_jamb.SetColumn(nRow ,"FLAG"    ,"I");
		ds_ee_jamb.SetColumn(nRow ,"INORDER" ,inorder);

	}else if(obj.id == "btn_jamb_minus") {
		nRow = ds_ee_jamb.GetCurrow();
		if(ds_ee_jamb.GetRowType(nRow) != "insert") {
			ds_ee_jamb.SetColumn(nRow ,"FLAG" ,"D");
		}else {
			ds_ee_jamb.DeleteRow(nRow);
		}
	}
}

// ROW추가시 INORDER 채번
function fn_get_inorder(ds) {
	var cnt = ds.GetRowCount();
	var inorder = 0;
	if(cnt == 0) return 1;
	for(var i = 0; i < cnt; i++) {
		var order = ds.GetColumn(i, "INORDER");
		var flag = ds.GetColumn(i, "FLAG");
		if(flag != "D") {
			if(inorder < order) inorder = order;
		}
	}
	inorder++;
	return inorder;
}

// 값이 NULL이면 true, NULL이 아니면 false
function fn_isNull(str) {
	if(length(trim(str)) < 1) return true;
	return false;
}

/********************************************************************************
* 기      능   : 입력  
********************************************************************************/
function fn_input() {
	// 필수 체크를 하지 않고 저장	
	var hhCnt  = ds_hh_dummy.GetRowCount();
	var mmCnt  = ds_mm_dummy.GetRowCount();
	var ccCnt  = ds_cc_dummy.GetRowCount();
	var eeCnt1 = ds_ee_hpb.GetRowCount();
	var eeCnt2 = ds_ee_jamb.GetRowCount();

	// 등록상태 T:임시저장
	if(hhCnt > 0) ds_head.SetColumn(0, "HRST",  "T");
	if(mmCnt > 0) ds_head.SetColumn(0, "MRST",  "T");
	if(ccCnt > 0) ds_head.SetColumn(0, "CRST",  "T");
	if(eeCnt1 > 0 || eeCnt2 > 0) ds_head.SetColumn(0, "ERST",  "T");

    fn_preSave();
}

function PS1120002A_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	if(bControl == "1" && indexOf("49,50,51,52", nChar) > -1) {
		tab_main.TabIndex = nChar-49;

	}else if(bShift == "1" && bControl == "1" && indexOf("83", nChar) > -1) {
		fn_save();

	}else if(bShift == "0" && bControl == "1" && indexOf("83", nChar) > -1) {
		fn_input();
	}
}
