function fn_query() 
{
	if(Tab00.TabIndex == 0)
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps12:PS1201003A_S1");
		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, true);
	}
	else if(Tab00.TabIndex == 1)
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps12:PS1201003A_S4");
		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list2=ds_list2"
			, ""
			, "fn_afterQuery2"
			, true);
	}

}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(Tab00.TabIndex == 0) fn_save1();
	else if(Tab00.TabIndex == 1) fn_save2();
}
/********************************************************************************
* 기      능   : 저장 - TAB0
********************************************************************************/
function fn_save1() {
	if(!ds_list.GetUpdate())
	{
		gfn_showAlert("CW00005");
		return;
	}

	if(ds_list.GetUpdate())
	{
		for(var i=0; i<ds_list.rowcount; i++)
		{
			if(ds_list.GetColumn(i,"IRUM") == "" || length(ds_list.GetColumn(i,"IRUM")) == 0)
			{
				gfn_showMsg("CW00001","이름");
				return;
			}
			if(ds_list.GetColumn(i,"DPT") == "" || length(ds_list.GetColumn(i,"DPT")) == 0)
			{
				gfn_showMsg("CW00001","부서");
				return;
			}
			if(ds_list.GetColumn(i,"JIKMU") == "" || length(ds_list.GetColumn(i,"JIKMU")) == 0)
			{
				gfn_showMsg("CW00001","직무");
				return;
			}
			if(ds_list.GetColumn(i,"GRADE") == "" || length(ds_list.GetColumn(i,"GRADE")) == 0)
			{
				gfn_showMsg("CW00001","등급");
				return;
			}
		}
	}

	Trace(ds_list.saveXML());
	tit_clearActionInfo();
	if(ds_list.GetUpdate())
	{
		tit_addSaveActionInfo(
			  "ps12:PS1201003A_I1"
			//, "ps12:PS1201003A_U1"
			, "ps12:PS1201003A_U1"
			, "ps12:PS1201003A_D1"
			, "FLAG"
			, ""
			, -1
			, "");
	}		
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 - TAB1
********************************************************************************/
function fn_save2() {
	if(!ds_list2.GetUpdate())
	{
		gfn_showAlert("CW00005");
		return;
	}

	if(ds_list2.GetUpdate())
	{
		for(var i=0; i<ds_list2.rowcount; i++)
		{
			if(ds_list2.GetColumn(i,"IRUM") == "" || length(ds_list2.GetColumn(i,"IRUM")) == 0)
			{
				gfn_showMsg("CW00001","이름");
				return;
			}
			if(ds_list2.GetColumn(i,"DPT") == "" || length(ds_list2.GetColumn(i,"DPT")) == 0)
			{
				gfn_showMsg("CW00001","부서");
				return;
			}
			if(ds_list2.GetColumn(i,"JIKMU") == "" || length(ds_list2.GetColumn(i,"JIKMU")) == 0)
			{
				gfn_showMsg("CW00001","직무");
				return;
			}
			if(ds_list2.GetColumn(i,"GRADE") == "" || length(ds_list2.GetColumn(i,"GRADE")) == 0)
			{
				gfn_showMsg("CW00001","등급");
				return;
			}
			if(ds_list2.GetColumn(i,"PSPID") == "" || length(ds_list2.GetColumn(i,"PSPID")) == 0)
			{
				gfn_showMsg("CW00001","프로젝트");
				return;
			}
			if(ds_list2.GetColumn(i,"JIDATE") == "" || length(ds_list2.GetColumn(i,"JIDATE")) == 0)
			{
				gfn_showMsg("CW00001","지정일자");
				return;
			}
			if(ds_list2.GetColumn(i,"STATE") == "" || length(ds_list2.GetColumn(i,"STATE")) == 0)
			{
				gfn_showMsg("CW00001","진행사항");
				return;
			}
			if(ds_list2.GetColumn(i,"JINAME") == "" || length(ds_list2.GetColumn(i,"JINAME")) == 0)
			{
				gfn_showMsg("CW00001","지정자");
				return;
			}
			if(ds_list2.GetColumn(i,"COMP") == "" || length(ds_list2.GetColumn(i,"COMP")) == 0)
			{
				gfn_showMsg("CW00001","시행사");
				return;
			}
		}
	}

	tit_clearActionInfo();
	if(ds_list2.GetUpdate())
	{
		tit_addSaveActionInfo(
			  "ps12:PS1201003A_I2"
			, "ps12:PS1201003A_U2"
			, "ps12:PS1201003A_D2"
			, "FLAG"
			, ""
			, -1
			, "");
	}		
	tit_callService(
        ""
        , ""
        , "ds_list2=ds_list2:A"
        , ""
        , ""
        , "fn_afterSave");
}
