/********************************************************************************
* 기      능   : 헤더 입력(+)버튼 클릭시
********************************************************************************/
function Tab00_tab1_btn_add_OnClick(obj)
{
	ds_list.fireevent = false;
	ds_list.AddRow();			
	ds_list.SetColumn(ds_list.currow,"FLAG","I");
	ds_list.SetColumn(ds_list.currow,"IRUM","");
	ds_list.SetColumn(ds_list.currow,"DPT","");
	ds_list.SetColumn(ds_list.currow,"JIKMU","");
	ds_list.SetColumn(ds_list.currow,"GRADE","");
	ds_list.fireevent = true;
}
/********************************************************************************
* 기      능   : 헤더 삭제(-)버튼 클릭시
********************************************************************************/
function Tab00_tab1_btn_del_OnClick(obj)
{
	var flag = ds_list.GetColumn(ds_list.row,"FLAG");
	if(length(trim(flag)) == 0 || flag == "U")
		ds_list.SetColumn(ds_list.row,"FLAG","D");
	else if(flag == "D")
		ds_list.SetColumn(ds_list.row,"FLAG","");
	else
		ds_list.DeleteRow(ds_list.row);
}
