* 기      능   : 헤더 입력(+)버튼 클릭시
********************************************************************************/
function Tab00_tab2_btn_add_OnClick(obj)
{
	ds_list2.fireevent = false;
	ds_list2.AddRow();			
	ds_list2.SetColumn(ds_list2.currow,"FLAG","I");
	ds_list2.SetColumn(ds_list2.currow,"IRUM","");
	ds_list2.SetColumn(ds_list2.currow,"DPT","");
	ds_list2.SetColumn(ds_list2.currow,"JIKMU","");
	ds_list2.SetColumn(ds_list2.currow,"GRADE","");
	ds_list2.SetColumn(ds_list2.currow,"COMP","1");
	ds_list2.fireevent = true;
}
/********************************************************************************
* 기      능   : 헤더 삭제(-)버튼 클릭시
********************************************************************************/
function Tab00_tab2_btn_del_OnClick(obj)
{
	var flag = ds_list2.GetColumn(ds_list2.row,"FLAG");

	if(flag == "I") ds_list2.DeleteRow(ds_list2.row);
	else if(flag == "D") ds_list2.SetColumn(ds_list2.row,"FLAG","");
    else ds_list2.SetColumn(ds_list2.row,"FLAG","D");



}
/********************************************************************************
* 기      능   : 헤더 데이타셋 변경시 - ds_list
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "LIFNR": //업체변경시 사무소 자동 세팅
			ds_list.SetColumn(nRow,"ZZACTSS",ds_lifnr2.GetColumn(ds_lifnr2.FindRow("CODE",ds_list.GetColumn(nRow,"LIFNR")),"ZZACTSS"));
			break;
		case "IPQTY":
			if(ds_list.GetOrgColumn(nRow,"IPQTY") > ds_list.GetColumn(nRow,"IPQTY"))
			{
				gfn_showAlert("CW00217","총지급수량");
				ds_list.SetColumn(nRow,"IPQTY",ds_list.GetOrgColumn(nRow,"IPQTY"));
				break;
			}
			else
			{
				var curqty = ds_list.GetOrgColumn(nRow,"CURQTY");
				var ipqty = ds_list.GetColumn(nRow,"IPQTY");
				var iporg = ds_list.GetColumn(nRow,"IPORG");
				ds_list.SetColumn(nRow,"CURQTY",parseInt(curqty)+(parseInt(ipqty) - parseInt(iporg)));
				ds_list.SetColumn(nRow,"ADDQTY",(parseInt(ipqty) - parseInt(iporg)));
			}
			if(length(trim(ds_list.GetColumn(nRow,"FLAG"))) == 0)
				ds_list.SetColumn(nRow,"FLAG","U");
			break;
	}
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 헤더 데이타셋 변경시 - ds_list2
********************************************************************************/
function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "IRUM_DPT": 
			ds_list2.SetColumn(nRow,"IRUM",ds_irum.GetColumn(ds_irum.FindRow("IRUM_DPT",ds_list2.GetColumn(nRow,"IRUM_DPT")),"IRUM"));
			ds_list2.SetColumn(nRow,"DPT",ds_irum.GetColumn(ds_irum.FindRow("IRUM_DPT",ds_list2.GetColumn(nRow,"IRUM_DPT")),"DPT"));
			ds_list2.SetColumn(nRow,"JIKMU",ds_irum.GetColumn(ds_irum.FindRow("IRUM_DPT",ds_list2.GetColumn(nRow,"IRUM_DPT")),"JIKMU"));
			ds_list2.SetColumn(nRow,"GRADE",ds_irum.GetColumn(ds_irum.FindRow("IRUM_DPT",ds_list2.GetColumn(nRow,"IRUM_DPT")),"GRADE"));
			break;
		case "STATE": 
			if(ds_list2.GetColumn(nRow,"FLAG") == "") ds_list2.SetColumn(nRow,"FLAG","U");
			break;
	}
	obj.fireevent = true;
}
