/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시
********************************************************************************/
function Tab00_tab2_grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 100;
	if (cal_temp.top > 400)
	{
		cal_temp.top = 400;
	}
	cal_temp.Left = rect[0] + 20;
	if (cal_temp.Left > 790)
	{
		cal_temp.Left = 790;
	}
	cal_temp.Visible = true;

	cal_temp.value = ds_list2.getColumn(nRow, "JIDATE");
	cal_temp.SetFocus();
}
