/********************************************************************************
* 기      능   : cell에 텍스트입력시 프로젝트정보 자동검색
********************************************************************************/
function Tab00_tab2_grd_list_OnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam,nRow,nCell,nPivotIndex)
{
	if(length(strPostText) > 5 && nCell = 6 && ds_list2.GetColumn(nRow,"FLAG") == "I")
	{
		currRow = nRow;
		fn_pspid(trim(strPostText));
	}
}
/********************************************************************************
* 기      능   : 그리드에서 포커스이동시 프로젝트정보 자동검색
********************************************************************************/
function Tab00_tab2_grd_list_OnKillFocus(obj)
{
	currRow = ds_list2.row;
	fn_pspid(ds_list2.GetColumn(ds_list2.row,"PSPID"));	
}
