/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterPspid(errCode, errMsg) {
	if(ds_pspid.RowCount() > 0)
	{
		ds_list2.SetColumn(currRow,"ZSITE_NM",ds_pspid.GetColumn(0,"ZSITE_NM"));
		ds_list2.SetColumn(currRow,"PRO_R",ds_pspid.GetColumn(0,"PRO_R"));
	}
	else
	{
		ds_list2.SetColumn(currRow,"PSPID","");
		ds_list2.SetColumn(currRow,"ZSITE_NM","");
		ds_list2.SetColumn(currRow,"PRO_R","");
	}
}
