/********************************************************************************
* 기      능   : 건설기술자현황 업로드
********************************************************************************/
function Tab00_tab1_btn_upload_OnClick(obj)
{
	Tab00.tab1.grd_list.Format = "Upload";

	Create("FileDialog", "_fileDialog_", "");
	var dialogObj = object("_fileDialog_");

	dialogObj.Type = "Open";
	dialogObj.FileName = "";
	dialogObj.Filter = "Microsoft Excel File(*.xlsx)|*.xlsx|Microsoft Excel File(*.xls)|*.xls|";
	dialogObj.open();

	if (length(dialogObj.FileName) == 0) {
		Destroy( "_fileDialog_" );
		return;
	}

	var sFileName = dialogObj.FilePath + "\\" + dialogObj.FileName;

	ext_ExcelImportByIndex(sFileName,null,"ds_excel",0,1);

	Tab00.tab1.grd_list.BindDataset = "";

	ufn_ExcelToList();

	Tab00.tab1.grd_list.BindDataset = "ds_list";

	Destroy( "_fileDialog_" );  // 불러오기후 dialog 제거	
}

function ufn_ExcelToList()
{
	ds_list.ClearData();


	for(i=1;i<ds_excel.rowcount;i++) {
		var nAddRow = ds_list.AddRow();
		var SearchRow = 0;
		ds_list.SetColumn(nAddRow,"FLAG","I");
		ds_list.SetColumn(nAddRow,"IRUM",ds_excel.GetColumn(i,0));	// 프로젝트

		Trace(ds_excel.GetColumn(i,0));
		Trace(ds_excel.GetColumn(i,1));
		Trace(ds_excel.GetColumn(i,2));
		Trace(ds_excel.GetColumn(i,3));
		SearchRow = ds_dpt.SearchRow("CODE == '"+ds_excel.getColumn(i,1)+"'");
		if(SearchRow < 0)
		{
			gfn_showAlert("CW00022", "부서");
			ds_list.ClearData();
			return;
		} else {
			ds_list.SetColumn(nAddRow,"DPT", ds_excel.GetColumn(i,1));			
	    }

	    if(ds_excel.GetColumn(i,2) != "1" && ds_excel.GetColumn(i,2) != "2" &&
		   ds_excel.GetColumn(i,2) != "3" && ds_excel.GetColumn(i,2) != "4" &&
		   ds_excel.GetColumn(i,2) != "5" && ds_excel.GetColumn(i,2) != "6")
		{     
			gfn_showAlert("CW00022", "직무분야");
		    ds_list.ClearData();
		    return;
		} else {
			ds_list.SetColumn(nAddRow,"JIKMU", ds_excel.GetColumn(i,2));	
		}



		if(ds_excel.GetColumn(i,3) != "1" && ds_excel.GetColumn(i,3) != "2" &&
		   ds_excel.GetColumn(i,3) != "3" && ds_excel.GetColumn(i,3) != "4" &&
		   ds_excel.GetColumn(i,3) != "5")
		{     
			gfn_showAlert("CW00022", "등급");
		    ds_list.ClearData();
		    return;
		} else {
			ds_list.SetColumn(nAddRow,"GRADE", ds_excel.GetColumn(i,3));	
		}




	}
}

