function Tab00_tab2_btn_template_OnClick(obj)
{
	gfn_exportExcel(grd_list3,G_USER_ID);
}

/********************************************************************************
* 기      능   : 건설기술자 현장등록 현황 업로드
********************************************************************************/
function Tab00_tab2_btn_upload_OnClick(obj)
{
	Tab00.tab2.grd_list.Format = "Upload";

	Create("FileDialog", "_fileDialog_", "");
	var dialogObj = object("_fileDialog_");

	dialogObj.Type = "Open";
	dialogObj.FileName = "";
	dialogObj.Filter = "Microsoft Excel File(*.xlsx)|*.xlsx|Microsoft Excel File(*.xls)|*.xls|";
	dialogObj.open();

	if (length(dialogObj.FileName) == 0) {
		Destroy( "_fileDialog_" );
		return;
	}

	var sFileName = dialogObj.FilePath + "\\" + dialogObj.FileName;

	ext_ExcelImportByIndex(sFileName,null,"ds_excel2",0,1);

	Tab00.tab2.grd_list.BindDataset = "";

	ufn_ExcelToList2();

	Tab00.tab2.grd_list.BindDataset = "ds_list2";

	Destroy( "_fileDialog_" );  // 불러오기후 dialog 제거
}

function ufn_ExcelToList2()
{
	ds_list.ClearData();


	for(i=1;i<ds_excel2.rowcount;i++) {
		var nAddRow = ds_list2.AddRow();
		var SearchRow = 0;

		ds_list2.SetColumn(nAddRow,"FLAG","I");
		ds_list2.SetColumn(nAddRow,"IRUM",ds_excel2.GetColumn(i,0));	// 이름

		SearchRow = ds_dpt.SearchRow("CODE == '"+ds_excel2.getColumn(i,1)+"'");
		if(SearchRow < 0)
		{
			gfn_showAlert("CW00022", "부서");
			ds_list2.ClearData();
			return;
		} else {
			ds_list2.SetColumn(nAddRow,"DPT", ds_excel2.GetColumn(i,1));


			ds_list2.SetColumn(nAddRow,"IRUM_DPT", ds_excel2.GetColumn(i,0)+"("+ds_dpt.GetColumn(SearchRow, "CODE_NAME")+")");	// 이름+부서
	    }

	    if(ds_excel2.GetColumn(i,2) != "1" && ds_excel2.GetColumn(i,2) != "2" &&
		   ds_excel2.GetColumn(i,2) != "3" && ds_excel2.GetColumn(i,2) != "4" &&
		   ds_excel2.GetColumn(i,2) != "5" && ds_excel2.GetColumn(i,2) != "6")
		{     
			gfn_showAlert("CW00022", "직무분야");
		    ds_list2.ClearData();
		    return;
		} else {
			ds_list2.SetColumn(nAddRow,"JIKMU", ds_excel2.GetColumn(i,2));	
		}



		if(ds_excel2.GetColumn(i,3) != "1" && ds_excel2.GetColumn(i,3) != "2" &&
		   ds_excel2.GetColumn(i,3) != "3" && ds_excel2.GetColumn(i,3) != "4" &&
		   ds_excel2.GetColumn(i,3) != "5")
		{     
			gfn_showAlert("CW00022", "등급");
		    ds_list2.ClearData();
		    return;
		} else {
			ds_list2.SetColumn(nAddRow,"GRADE", ds_excel2.GetColumn(i,3));	
		}

		ds_list2.SetColumn(nAddRow,"PSPID",ds_excel2.GetColumn(i,4));	// 프로젝트


		if(ds_excel2.GetColumn(i,5) != "1" && ds_excel2.GetColumn(i,5) != "2" &&
		   ds_excel2.GetColumn(i,5) != "3" && ds_excel2.GetColumn(i,5) != "4")
		{     
			gfn_showAlert("CW00022", "시행사");
		    ds_list2.ClearData();
		    return;
		} else {
			ds_list2.SetColumn(nAddRow,"COMP", ds_excel2.GetColumn(i,5));	
		}

		if( length(ds_excel2.GetColumn(i,6)) != 8 ) 
		{
			gfn_showAlert("CW00008", "지정일자");
			ds_list2.ClearData();
			return;
		} else {
			ds_list2.SetColumn(nAddRow,"JIDATE", ds_excel2.GetColumn(i,6));	
		}

		if(ds_excel2.GetColumn(i,7) != "1" && ds_excel2.GetColumn(i,7) != "2")
		{     
			gfn_showAlert("CW00022", "진행사항");
		    ds_list2.ClearData();
		    return;
		} else {
			ds_list2.SetColumn(nAddRow,"STATE", ds_excel2.GetColumn(i,7));	
		}

		ds_list2.SetColumn(nAddRow,"JINAME", ds_excel2.GetColumn(i,8));	


	}

	Trace(ds_list2.SaveXML());
}
