function fn_OnLoadCompleted(obj) 
{
	gfn_initForm(false);
	user_grp = gfn_getUserGroup();

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	Tab00.tab1.btn_add.Enable = false;
	Tab00.tab1.btn_del.Enable = false;
	Tab00.tab1.btn_add2.Enable = false;
	Tab00.tab1.btn_del2.Enable = false;

	//헤더정보는 설치부만 수정 가능함
	if(user_grp == "40")
		Tab00.tab1.grd_list1.Editable = true;
	else
		Tab00.tab1.grd_list1.Editable = false;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1201004A_S1");
	tit_addSearchActionInfo("ps12:PS1201004B_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_lifnr=ds_lifnr ds_lifnr2=ds_lifnr ds_pms=ds_pm"
		, ""
		, "fn_afterLifnr"
		, true);
}
