function fn_query() 
{
	if(user_grp == '10' && length(trim(ds_cond.GetColumn(0,"LIFNR"))) == 0 ) 
	{
		gfn_showMsg("ZW00003");
		return;
	} 

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1201004A_S2");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회(디테일)
********************************************************************************/
function fn_query2() 
{
	ds_cond2.ClearData();
	ds_cond2.AddRow();
