	ds_cond2.SetColumn(0,"GUBUN",ds_cond.GetColumn(0,"GUBUN"));
	ds_cond2.SetColumn(0,"PSPID",ds_cond.GetColumn(0,"PSPID"));
	ds_cond2.SetColumn(0,"ZZACTSS",ds_cond.GetColumn(0,"ZZACTSS"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1201004A_S3");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}
/********************************************************************************
* 기      능   : 조회(인계이력)
