/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(!ds_head.GetUpdate() && !ds_list.GetUpdate())
	{
		gfn_showAlert("CW00005");
		return;
	}
	if(ds_head.GetUpdate() && ds_list.GetUpdate())
	{
		var arr = ["총지급정보","상세정보"];
		gfn_showMsg("CW00216",arr);
		return;
	}
	if(ds_head.GetUpdate())
	{
		for(var i=0; i<ds_head.rowcount; i++)
		{
			if(length(trim(ds_head.GetColumn(i,"FLAG"))) > 0)
			{
				if(ds_head.GetColumn(i,"LIFNR") == "")
				{
					gfn_showMsg("CW00001","협력업체");
					return;
				}
				if(ds_head.GetColumn(i,"FLAG") == "I" && (ds_head.GetColumn(i,"IPQTY") == "" || ds_head.GetColumn(i,"IPQTY") == "0"))
				{
					gfn_showMsg("CW00001","총지급수량");
					return;
				}
			}	
		}
	}
	if(ds_list.GetUpdate())
	{
		for(var i=0; i<ds_list.rowcount; i++)
		{
			if(ds_list.GetColumn(i,"PSPID") == "" || ds_list.GetColumn(i,"ZSITE_NM") == "")
			{
				gfn_showMsg("CW00001","프로젝트");
				return;
			}
			if(ds_list.GetColumn(i,"FLAG") == "I" && length(ds_list.GetColumn(i,"TEMNO")) == 0)
			{
				gfn_showMsg("CW00001","작업팀장");
				return;
			}
			if(ds_list.GetColumn(i,"FLAG") == "I" && (ds_list.GetColumn(i,"IPQTY") == "" || ds_list.GetColumn(i,"IPQTY") == "0"))
			{
				gfn_showMsg("CW00001","투입수량");
				return;
			}
		}
	}

	tit_clearActionInfo();
	if(ds_head.GetUpdate())
	{
		tit_addSaveActionInfo(
			  "ps12:PS1201004A_I1"
			, "ps12:PS1201004A_U1"
			, "ps12:PS1201004A_D1"
			, "FLAG"
			, ""
			, -1
			, "");
	}		
	if(ds_list.GetUpdate())
	{
		tit_addSaveActionInfo(
			  "ps12:PS1201004A_I2"
			, "ps12:PS1201004A_U2"
			, "ps12:PS1201004A_D2"
			, "FLAG"
			, ""
			, -1
			, "");
	}		
	tit_callService(
        ""
        , ""
        , "ds_head=ds_head:A ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}
