/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print1() 
{
	ds_head_p.ClearData();
	ds_head_p.Copy(ds_head);
	var zzactss = "";
	for(var i=0; i<ds_head_p.rowcount; i++)
	{
		if(zzactss == ds_head_p.GetColumn(i,"ZZACTSS"))
		{
			ds_head_p.SetColumn(i,"ZZACTSS_NM","");
		}
		zzactss = ds_head_p.GetColumn(i,"ZZACTSS");
	}
	Dialog("PS12::PS1201004P.xml", "gbn=a", -1, -1, "Resize=true");
}
