	ds_head.SetColumn(ds_head.currow,"LIFNR","");
	ds_head.SetColumn(ds_head.currow,"IPQTY","0");
	ds_head.SetColumn(ds_head.currow,"IPORG","0");
	ds_head.SetColumn(ds_head.currow,"BANQTY","0");
	ds_head.SetColumn(ds_head.currow,"INQTY","0");
	ds_head.SetColumn(ds_head.currow,"OUTQTY","0");
	ds_head.SetColumn(ds_head.currow,"LOSQTY","0");
	ds_head.SetColumn(ds_head.currow,"LOSRATE","0");
	ds_head.SetColumn(ds_head.currow,"CURQTY","0");
	ds_head.SetColumn(ds_head.currow,"ZZACTSS","");
	ds_head.fireevent = true;
}
/********************************************************************************
* 기      능   : 헤더 삭제(-)버튼 클릭시
********************************************************************************/
function Tab00_tab1_btn_del_OnClick(obj)
{
	var flag = ds_head.GetColumn(ds_head.row,"FLAG");
	if(length(trim(flag)) == 0 || flag == "U")
		ds_head.SetColumn(ds_head.row,"FLAG","D");
	else if(flag == "D")
		ds_head.SetColumn(ds_head.row,"FLAG","");
	else
		ds_head.DeleteRow(ds_head.row);
}
