function Tab00_tab1_btn_del2_OnClick(obj)
{
	if( ds_list.GetColumn(ds_list.row,"PSPID") == "00000Z" || length(trim(ds_list.GetColumn(ds_list.row,"FLAG"))) == 0) {
		gfn_showMsg("CW00072");
		return;
	}
	else
	{
		ds_list.SetColumn(ds_list.row,"FLAG","");
		ip_qty = ds_list.GetColumn(ds_list.row,"IPQTY");
		ds_list.SetColumn(ds_list.row,"IPQTY","0");
		ds_list.DeleteRow(ds_list.row);
	}
}
