function ds_head_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "LIFNR": //업체변경시 사무소 자동 세팅
			ds_head.SetColumn(nRow,"ZZACTSS",ds_lifnr2.GetColumn(ds_lifnr2.FindRow("CODE",ds_head.GetColumn(nRow,"LIFNR")),"ZZACTSS"));
			break;
		case "IPQTY":
			if( 1 > ds_head.GetColumn(nRow,"IPQTY"))
			{
				gfn_showAlert("CW00217"," 1 ");
				ds_head.SetColumn(nRow,"IPQTY",ds_head.GetOrgColumn(nRow,"IPQTY"));
				ds_head.SetColumn(nRow,"CURQTY",ds_head.GetOrgColumn(nRow,"CURQTY"));
				ds_head.SetColumn(nRow,"ADDQTY",ds_head.GetOrgColumn(nRow,"ADDQTY"));
				break;
			}
			else
			{
				var curqty = ds_head.GetOrgColumn(nRow,"CURQTY");
				var ipqty = ds_head.GetColumn(nRow,"IPQTY");
				var iporg = ds_head.GetColumn(nRow,"IPORG");
				ds_head.SetColumn(nRow,"IPQTY",parseInt(varNewValue)+parseInt(iporg));
				ds_head.SetColumn(nRow,"CURQTY",parseInt(curqty)+parseInt(ipqty));
				ds_head.SetColumn(nRow,"ADDQTY",parseInt(ipqty));
			}
			if(length(trim(ds_head.GetColumn(nRow,"FLAG"))) == 0)
				ds_head.SetColumn(nRow,"FLAG","U");
			break;
	}
	obj.fireevent = true;

/*	
	obj.fireevent = false;
	switch( strColumnID ) {
		case "LIFNR": //업체변경시 사무소 자동 세팅
			ds_head.SetColumn(nRow,"ZZACTSS",ds_lifnr2.GetColumn(ds_lifnr2.FindRow("CODE",ds_head.GetColumn(nRow,"LIFNR")),"ZZACTSS"));
