*/	
}
/********************************************************************************
* 기      능   : 디테일 데이타셋 변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	var idx = ds_list.FindRow("PSPID","00000Z"); //사무실보관의 row index
	var ipqty = ds_list.GetColumn(idx,"IPQTY");  //사무실보관의 투입가능수량
	var curqty = ds_list.GetColumn(idx,"CURQTY");  //사무실보관의 현재수량
	var flag = ds_list.GetColumn(nRow,"FLAG");
	if(flag == "I") ip_qty = ds_list.GetColumn(nRow,"IPQTY");

	switch( strColumnID ) {
		case "IPQTY":
			if(ip_qty < 0)
			{
				gfn_showMsg("CW00188","투입수량");
				ds_list.SetColumn(nRow,"IPQTY","");
				break;
			}
			if(flag == "I" && parseInt(ipqty) - parseInt(ip_qty) < 0)
			{
				var arr = ["투입수량", "사무실보관 수량"];
				gfn_showMsg("CW00190",arr);
				ds_list.SetColumn(nRow,"IPQTY","");
				break;
			}
			else
			{
				if(length(trim(flag)) == 0)
				{  //현장투입시
					ds_list.SetColumn(idx,"IPQTY",parseInt(ipqty) + parseInt(ip_qty));
					ds_list.SetColumn(idx,"CURQTY",parseInt(curqty) + parseInt(ip_qty));
				}	
				else if(flag == "I")
				{
					ds_list.SetColumn(nRow,"CURQTY",ip_qty);
					ds_list.SetColumn(idx,"IPQTY",parseInt(ipqty) - parseInt(ip_qty));
					ds_list.SetColumn(idx,"CURQTY",parseInt(curqty) - parseInt(ip_qty));
				}	
				if(ds_list.GetOrgColumn(idx,"IPQTY") == ds_list.GetColumn(idx,"IPQTY") && ds_list.GetOrgColumn(idx,"CURQTY") == ds_list.GetColumn(idx,"CURQTY"))
					ds_list.SetColumn(idx,"FLAG","");
				else
					ds_list.SetColumn(idx,"FLAG","U");
			}
			break;
		case "PSPID":
			nRows = nRow;
			fn_getPspid(varNewValue);
			break;
	}
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 조회 (프로젝트 정보)
********************************************************************************/
function fn_getPspid(pspid) {
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1201004A_S4");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_pspid=ds_pspid"
        , "PSPID="+quote(pspid)
        , "fn_afterPspid"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback 
********************************************************************************/
function fn_afterPspid(errCode, errMsg) {
	if(ds_pspid.RowCount() == 0)
	{
		gfn_showMsg("CW00038","프로젝트");
		ds_list.SetColumn(nRows,"PSPID","");
		ds_list.SetColumn(nRows,"ZSITE_NM","");
	}
	else
	{
		ds_list.SetColumn(nRows,"ZSITE_NM",ds_pspid.GetColumn(0,"ZSITE_NM"));
	}
}
