function fn_OnLoadCompleted(obj) 
{
	gfn_initForm(false);
	user_grp = gfn_getUserGroup();

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);


	Tab00.tab1.btn_add2.Enable = false;
	Tab00.tab1.btn_del2.Enable = false;

	if(G_USER_ID == "2019094" or G_USER_ID == "2025965" or G_USER_ID == "2022702"){ // 조용준 GJ 신인재 SW -> 관리자
    	F_ADMIN_YN = "Y";
	}

	//날짜 정보
	ed_cdat_f.value = substr(gfn_getCurrDate(),0,6)+"01";;  //substr(gfn_getCurrDate(),0,6);
    ed_cdat_t.value   = gfn_getCurrDate();  //substr(gfn_getCurrDate(),0,6);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1201005A_S1");
	tit_addSearchActionInfo("ps12:PS1201005B_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_lifnr=ds_lifnr ds_lifnr2=ds_lifnr ds_pms=ds_pm"
		, ""
		, "fn_afterLifnr"
		, true);
}
