function fn_query() 
{
	if(user_grp == '10' && length(trim(ds_cond.GetColumn(0,"LIFNR"))) == 0 ) 
	{
		gfn_showMsg("ZW00003");
		return;
	} 

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1201005A_S2");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회(디테일)
********************************************************************************/
function fn_query2() 
{
    ds_cond2.ClearData();
	ds_cond2.AddRow();
	ds_cond2.SetColumn(0,"LIFNR",ds_head.GetColumn(ds_head.row,"LIFNR"));
	ds_cond2.SetColumn(0,"PSPID",ds_cond.GetColumn(0,"PSPID"));
	ds_cond2.SetColumn(0,"ZZACTSS",ds_cond.GetColumn(0,"ZZACTSS"));

	if(length(ds_cond.GetColumn(0,"CDAT_T")) > 0 ){
	 ds_cond2.SetColumn(0,"CDAT_T",ds_cond.GetColumn(0,"CDAT_T"));
	 ds_cond2.SetColumn(0,"CDAT_F",ds_cond.GetColumn(0,"CDAT_F"));
	}
	if(length(ds_cond.GetColumn(0,"RDAT_T")) > 0 ){
	 ds_cond2.SetColumn(0,"RDAT_T",ds_cond.GetColumn(0,"RDAT_T"));
	 ds_cond2.SetColumn(0,"RDAT_F",ds_cond.GetColumn(0,"RDAT_F"));
	}



	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1201005A_S3");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);

}
