/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

    for(var i=0; i<ds_list.rowcount; i++)
    {
      if(ds_list.GetColumn(i, "GBN") == 'U' && (length(ds_list.GetColumn(i, "RDATE")) < 8 ||  ds_list.GetColumn(i, "RDATE") < 19990101))
      {
         gfn_showMsg("CW00001","회수일");
		 return;
      }

     }

    //DELETE
    ds_list.Filter("GBN='D'");
	ds_list_d.CopyF(ds_list);
	ds_list.UnFilter();

    // UPDATE
	ds_list.Filter("GBN='U'");
	ds_list_u.CopyF(ds_list);
	ds_list.UnFilter();

	if(ds_list_d.rowcount > 0)  
    {

        tit_clearActionInfo();
        tit_addMultiActionInfo("ps12:PS1201005A_D1");
        tit_addMultiActionInfo("ps12:PS1201005A_D2");
        tit_callService(
		  ""	
		, ""
		, "ds_list_d=ds_list_d"
		, ""
		, ""
		, ""//"fn_afterSave"
		, ""
		, ""
		, true  // ""
      );

     }

    if(ds_list_u.rowcount > 0)  
    {
        tit_clearActionInfo();
        tit_addMultiActionInfo("ps12:PS1201005A_U1");
        tit_callService(
		  ""	
		, ""
		, "ds_list_u=ds_list_u"
		, ""
		, ""
		, ""//"fn_afterSave"
		, ""
		, ""
		, true  // ""
      );

     }  

     fn_query();
}
