/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	if( user_grp == "10" )
	{
		ds_lifnr.filter("CODE == " + quote( G_VEND_CODE_E) );
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;

		ds_pms.insertRow(0);
		ds_pms.setColumn(0, "CODE", "");
		ds_pms.setColumn(0, "CODE_NAME", "");
		ds_pms.setColumn(0, "LIFNR", G_VEND_CODE_E);
		ds_pms.Filter("CODE=='' || LIFNR=="+quote(G_VEND_CODE_E));
	}
	else if ( user_grp == "40" )
	{
		cb_lifnr.Enable = true;
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_lifnr.value = "";
	}	
	else
	{
		cb_lifnr.value = "";
		cb_lifnr.Enable = false;
	}
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
