/********************************************************************************
* 기      능   : 디테일 데이타셋 변경시
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
  obj.fireevent = false;

    var sum  = parseInt(ds_list.GetColumn(nRow,"IPQTY")) + parseInt(ds_list.GetColumn(nRow,"RUQTY")) ; //보유량
    var sum2 = parseInt(ds_list.GetColumn(nRow,"BANQTY")) + parseInt(ds_list.GetColumn(nRow,"LOSQTY")) ;  //회수,폐기량

   switch( strColumnID ) {
    case "BANQTY" :

       if(sum < sum2)
       {
           var arr = ["회수/폐기 수량", "보유수량"];
		   gfn_showMsg("CW00190",arr);
		   ds_list.SetColumn(ds_list.row,"GBN","X");
		   ds_list.SetColumn(ds_list.row,"BANQTY",0);
		   ds_list.SetColumn(ds_list.row,"LOSQTY",0);
		   break;
       }

       if(ds_list.GetColumn(nRow,"BANQTY") > 0 || ds_list.GetColumn(nRow,"LOSQTY") > 0 )
       {
          ds_list.SetColumn(ds_list.row,"GBN","U");
          break;
       }
       else
       {
          ds_list.SetColumn(ds_list.row,"GBN","X");
          break;
       } 

    case "LOSQTY" :

       if(sum < sum2)
       {
           var arr = ["회수/폐기 수량", "보유수량"];
		   gfn_showMsg("CW00190",arr);
		   ds_list.SetColumn(ds_list.row,"GBN","X");
		   ds_list.SetColumn(ds_list.row,"BANQTY",0);
		   ds_list.SetColumn(ds_list.row,"LOSQTY",0);

		 break;
       }
       if(ds_list.GetColumn(nRow,"BANQTY") > 0 || ds_list.GetColumn(nRow,"LOSQTY") > 0 )
       {
          ds_list.SetColumn(ds_list.row,"GBN","U");
          break;
       }
       else
       {
          ds_list.SetColumn(ds_list.row,"GBN","X");
          break;
       }

       case "BIGO" :

         ds_list.SetColumn(ds_list.row,"GBN","U");
         break;

       case "RDATE" :

         ds_list.SetColumn(ds_list.row,"GBN","U");
         break;  

   }
   obj.fireevent = true;
}
