/********************************************************************************
* 기      능   : 디테일 그리드 더블클릭시
********************************************************************************/
function Tab00_tab1_grd_list2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 8)
	{ // 상세 신청조회화면


	    var args  = ' F_CDATE='+quote(ds_list.getColumn(nRow, "CDATE"));
            args += ' F_LIFNR='+quote(ds_list.getColumn(nRow, "LIFNR"));
            args += ' F_PSPID='+quote(ds_list.getColumn(nRow, "PSPID"));

	    gfn_openPopup("PS1201005F", true, args);
	}

	else if(nCell == 18)
	{
		if(ds_list.GetColumn(nRow,"CURQTY") == "0" || ds_list.GetColumn(nRow,"CURQTY") == "")
		{
			gfn_showMsg("CW00053","인계");
			return;
		}	
		if(length(trim(ds_list.GetColumn(nRow,"FLAG"))) == 0)
		{
			gfn_openPopup("PS1201005B",true);
		}
	}	
}
/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(user_grp == "40" || user_grp == "30"){
    ds_cond.SetColumn(0,"LIFNR",''); }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_lifnr.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_lifnr.Filter("ZZACTSS ==" +quote(arr));
    }
}

