/********************************************************************************
* 기      능   : 세부사항 조회
********************************************************************************/
function ufn_headquery(nRow) 
{

	ds_head.ClearData();
	//ds_cond2.ClearData();
	gfn_initDs(ds_cond2, true);	

	//ds_list_tmp.Copy(ds_list);
	ds_cond2.setColumn(0, "PSPID", ds_list.GetColumn(nRow,"PSPID"));
	ds_cond2.setColumn(0, "POSID", ds_list.GetColumn(nRow,"POSID"));
	ds_cond2.setColumn(0, "SUP_DT", ds_list.GetColumn(nRow,"SUP_DT"));
	ds_cond2.setColumn(0, "GBN", ds_list.GetColumn(nRow,"GBN"));

/*	
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps04:PS0410001B_S2");      		
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_cond2=ds_cond2"
			, ""
			, ""  //"fn_save_afterQuery1"
			, ""
            , ""
			, true
			);	
*/	

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1202001A_S3", null, true);
	tit_callService(
        ""
        , ""
        , "ds_cond2=ds_cond2"
        , "ds_head=ds_head"
        , ""
        , ""  //"fn_afterQuery2"
        , ""
        , ""
        , true);

	Button1.Visible = false;
	ufn_objUse("first");


	//자기가 지원한것만 수정 가능
	if (ds_head.GetColumn(0,"SUP_CD") == G_USER_ID)
	{
		//btn_chg.visible = true;
		btn_chg.Enable  = true;
	}
	else
	{
		//btn_chg.visible = false;
		btn_chg.Enable  = false;
	}



}


