/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() 
{

    //헤더 부분 가져오기
	//ufn_hedquery();

	//지원일 필수 체크
	if ( !gfn_checkFromToObj(cal_supdtf, cal_supdtt, true, st_supdt.value, 0, false) ) {
			return;
	}


	//프로젝트 필수체크
    //if(gfn_isNull(ed_pspid,   true, st_pjt.value))	  return;

	//호기 필수체크
	//if(gfn_isNull(ed_posid, true, st_posid.value))  return;	

	//기계/전기 필수체크
	//if(gfn_isNull(cb_gbn,  true, st_gbn.value)) return;



    // ======================그리드 조회=================    	    
	tit_clearActionInfo();		
	tit_addSearchActionInfo("ps12:PS1202001A_S1");  
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"  
        , ""
        , ""
        , true);



	Button1.Visible = false;


}

