/********************************************************************************
* 기      능   : 에디터 박스 입력가능 여부
********************************************************************************/
function ufn_objUse(chk) 
{

	if(chk == "new") 
	{	
		ed_pspid.Readonly 	= true;
		ed_posid.Readonly 	= true;
		ed_nam.Readonly   	= true;
		ed_lifnr.Readonly 	= true;
		ed_temnm.Readonly 	= true;
		ed_supnm.Readonly 	= true;
		ed_spec1.Readonly 	= true;
		ed_spec2.Readonly 	= true;
		ed_spec3.Readonly 	= true;
		me_mh.Readonly	  	= false;
		txa_issue.Readonly  = false;
		txa_conduct.Readonly = false;
		txa_bigo.Readonly	= false;

		cb_gbn.enable		= true;
		cal_supdt.enable	= true;
		cb_cause1.enable	= true;
		cb_cause2.enable	= true;
		cb_cause3.enable	= true;
		cb_result.enable	= true;
		cb_mcu1.enable		= true;
		cb_mcu2.enable		= true;
		cb_inverter.enable 	= true;	

		save_chk = 'X';
	}

	else if(chk == "first")
	{	
		ed_pspid.Readonly 	= true;
		ed_posid.Readonly 	= true;
		ed_nam.Readonly   	= true;
		ed_lifnr.Readonly 	= true;
		ed_temnm.Readonly 	= true;
		ed_supnm.Readonly 	= true;
		ed_spec1.Readonly 	= true;
		ed_spec2.Readonly 	= true;
		ed_spec3.Readonly 	= true;
		me_mh.Readonly	  	= true;
		txa_issue.Readonly  = true;
		txa_conduct.Readonly = true;
		txa_bigo.Readonly	= true;

		cb_gbn.enable		= false;
		cal_supdt.enable	= false;
		cb_cause1.enable	= false;
		cb_cause2.enable	= false;
		cb_cause3.enable	= false;
		cb_result.enable	= false;
		cb_mcu1.enable		= false;
		cb_mcu2.enable		= false;
		cb_inverter.enable 	= false;	

		save_chk = '';

	}
	else if(chk == "chg")
	{		
		ed_pspid.Readonly 	= true;
		ed_posid.Readonly 	= true;
		ed_nam.Readonly   	= true;
		ed_lifnr.Readonly 	= true;
		ed_temnm.Readonly 	= true;
		ed_supnm.Readonly 	= true;
		ed_spec1.Readonly 	= true;
		ed_spec2.Readonly 	= true;
		ed_spec3.Readonly 	= true;
		me_mh.Readonly	  	= false;
		txa_issue.Readonly  = false;
		txa_conduct.Readonly = false;
		txa_bigo.Readonly	= false;

		cb_gbn.enable		= false;
		cal_supdt.enable	= false;
		cb_cause1.enable	= true;
		cb_cause2.enable	= true;
		cb_cause3.enable	= true;
		cb_result.enable	= true;
		cb_mcu1.enable		= true;
		cb_mcu2.enable		= true;
		cb_inverter.enable 	= true;	

		save_chk = '';

	}

}



