/********************************************************************************
* 기      능   : 호기정보 읽어 오기
********************************************************************************/
function ed_posid_OnChanged(obj,strText)
{

	//ds_cond2.ClearData();
	gfn_initDs(ds_cond2, true);	
	//ds_list_tmp.Copy(ds_list);
	ds_cond2.setColumn(0, "PSPID", ds_head.GetColumn(0,"PSPID"));
	ds_cond2.setColumn(0, "POSID", ds_head.GetColumn(0,"POSID"));


	tit_clearActionInfo();		
	tit_addSearchActionInfo("ps12:PS1202001A_S4");  
	tit_callService(
          ""
        , ""
        , "ds_cond2=ds_cond2"
        , "ds_head_tmp=ds_head_tmp"
        , ""
        , ""   //"fn_afterQuery"  
        , ""
        , ""
        , true);   

	ds_head.setColumn(0, "TEM_NM", ds_head_tmp.GetColumn(0,"TEM_NM"));
	ds_head.setColumn(0, "LIFNR", ds_head_tmp.GetColumn(0,"LIFNR"));
	ds_head.setColumn(0, "SPEC1", ds_head_tmp.GetColumn(0,"SPEC1"));
	ds_head.setColumn(0, "SPEC2", ds_head_tmp.GetColumn(0,"SPEC2"));
	ds_head.setColumn(0, "SPEC3", ds_head_tmp.GetColumn(0,"SPEC3"));


}




