/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{

	//프로젝트 필수체크
    if(gfn_isNull(ed_pspid,   true, Static4.value))	  return;

	//호기 필수체크
	if(gfn_isNull(ed_posid, true, Static12.value))  return;	

	//기계/전기 필수체크
	if(gfn_isNull(cb_gbn,  true, Static21.value)) return;

	//지원일 필수체크
	if(gfn_isNull(cal_supdt,  true, Static1.value)) return;


//alert(ds_head.SaveXML());	

	if ( save_chk == 'X' )
	{
		tit_clearActionInfo();
		tit_addSingleActionInfo("ps12:PS1202001A_I1");
		tit_callService(
			""
			, ""
			, "ds_head=ds_head"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true  


			);	
	}
	else
	{
		tit_clearActionInfo();
		tit_addSelectExActionInfo("ps12:PS1202001A_S5", "ps12:PS1202001A_I1", "ps12:PS1202001A_U1");
		tit_callService(
			""
			, ""
			, "ds_head=ds_head"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true  


			);		
	}

	gfn_initDs(ds_head, true);
	ufn_objUse("first");



}


/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{
	//삭제시 특정행선택시 가능
	if (ed_pspid.Text == '')
	{
		gfn_showMsg("CW00013");				
		return;
	}

	//지원자 본인만이 삭제 가능	
	if ( ds_head.GetColumn(0,"SUP_CD") != G_USER_ID )
	{
		//alert("1");
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "지원자 본인만이 삭제가 가능합니다.";
		return;
	}

	//alert("2");
	tit_clearActionInfo();
	tit_addSingleActionInfo("ps12:PS1202001A_ID");
	tit_callService(
		""
		, ""
		, "ds_head=ds_head"
		, ""
		, ""
		, "fn_afterDelete"
		, ""
		, ""
		, true  


		);		

	gfn_initDs(ds_head, true);
	ufn_objUse("first");


}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) 
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");		
		fn_query();
	}
}






/********************************************************************************
* 기      능   : 필터(mcu1에 따른 mcu2)
********************************************************************************/
function cb_mcu1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//ds_perno.filter("RETIF!='X' && ( TEMNO== 'T' || TEMNO==" + quote( temno) + ")"  );

	//alert(strCode);
	if ( length(strCode) == 0 )
	{
		ds_mcu2.FILTER("GBN=='돌겠다'");
	}
	else
	{
		ds_mcu2.FILTER("GBN==" + quote( strCode));
	}
}



/********************************************************************************
* 기      능   : 리스트의 타이틀 클릭  
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		return;
	}
	else if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}		
}
