function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	gfn_initDs(ds_cond, true);	

	var firstDay = substr(gfn_getCurrDate(),0,6)+"01";
	var lastDay = gfn_getCurrDate();
	cal_datef.value = firstDay;
	cal_datet.value = lastDay;



}





/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}



/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query()  //fn_query1() 
{

	// 필수 입력사항 및 일자 체크
	if (ed_pspid.Text == '')
	{ 	
		if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) )
		{       
			return;
		}
		else
		{
			if(gfn_isNull(cal_datef, true, '생성일자 또는 프로젝트번호는 필수입니다.')) return;
		}

		btn_add.Enable = false;
		btn_del.Enable = false;

	}

	//프로젝트 번호가 있어야지 추가가 가능함
	else 
	{
		btn_add.Enable = true;
		btn_del.Enable = true;
    }



	// 조회 처리	
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1203001A_S1");     	
	tit_addSearchActionInfo("ps12:PS1203001A_S5");     	
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"		
		, "ds_list=ds_list ds_posid=ds_posid"
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);



}


/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() 
{
	close();
}


/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	// 메시지 처리
	gfn_showSearchMsg(grd_list, errCode, errMsg);

	ds_list.Reset();


}



/********************************************************************************
* 기      능   : (+)버튼 클릭시
********************************************************************************/
function btn_add_OnClick(obj)
{

	tit_clearActionInfo();	
	//tit_addSearchActionInfo("ps04:PS0420001A_C5"); 	
	tit_addSearchActionInfo("ps12:PS1203001A_S2"); 	
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_posid=ds_posid"
		, ""
		, ""   //"fn_afterQuery"
		, ""
		, ""
		, true);





	ds_list.fireevent = false;
	ds_list.AddRow();	

	ds_list.SetColumn(ds_list.currow, "GBN", "X"  );			


	ds_list.fireevent = true;



}


/********************************************************************************
* 기      능   : (-)버튼 클릭시
********************************************************************************/
function btn_del_OnClick(obj)
{

	if ( ds_list.getColumn(ds_list.row, "GBN") == 'X' )
	{
		ds_list.DeleteRow(ds_list.row);
	}

}


/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 현재 처리할 format 기억
		gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}



/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 호기를 변경시 데이타 읽어옮
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{


	if ( strColumnID == 'POSID' )
	{

		//=========== 차수관리로 인하여 제외 ==========//
		/*
		var rtval ;
        rtval = ds_list.FindRow("POSID",varNewValue ,0, ds_list.rowcount);	        
        if ( rtval != -1  and rtval != nRow )
        {
			var arr = [parseInt(rtval) + 1, "호기는 이미 선택되었으니 호기"];
			gfn_showMsg("CW00055", arr);
			ds_list.SetColumn(nRow, "POSID",   "" );
			return;
		}
		*/
        //=============================================//



		ds_cond.SetColumn(0,"POSID",varNewValue);

		tit_clearActionInfo();		    
		tit_addSearchActionInfo("ps12:PS1203001A_S3");         
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list_tmp=ds_list_tmp"
			, ""
			, "" //"fn_afterQuery"
			, ""
			, ""
			, true);		

		obj.fireevent = false;

		ds_list.SetColumn(ds_list.currow, "CHK",   ds_list_tmp.getColumn(0, "CHK"));			
		ds_list.SetColumn(ds_list.currow, "PSPID", ds_list_tmp.getColumn(0, "PSPID"));
        ds_list.SetColumn(ds_list.currow, "POSID", ds_list_tmp.getColumn(0, "POSID"));
        ds_list.SetColumn(ds_list.currow, "SEQNO", ds_list_tmp.getColumn(0, "SEQNO"));

		ds_list.SetColumn(ds_list.currow, "ZSITE_NM", ds_list_tmp.getColumn(0, "ZSITE_NM"));
		ds_list.SetColumn(ds_list.currow, "ZSPEC1", ds_list_tmp.getColumn(0, "ZSPEC1"));
		ds_list.SetColumn(ds_list.currow, "ZSPEC2", ds_list_tmp.getColumn(0, "ZSPEC2"));

		ds_list.SetColumn(ds_list.currow, "SHKASC_X", ds_list_tmp.getColumn(0, "SHKASC_X"));		
		ds_list.SetColumn(ds_list.currow, "SHKASC_Y", ds_list_tmp.getColumn(0, "SHKASC_Y"));
		ds_list.SetColumn(ds_list.currow, "SHKASC_Z", ds_list_tmp.getColumn(0, "SHKASC_Z"));

		ds_list.SetColumn(ds_list.currow, "SHKDESC_X", ds_list_tmp.getColumn(0, "SHKDESC_X"));		
		ds_list.SetColumn(ds_list.currow, "SHKDESC_Y", ds_list_tmp.getColumn(0, "SHKDESC_Y"));
		ds_list.SetColumn(ds_list.currow, "SHKDESC_Z", ds_list_tmp.getColumn(0, "SHKDESC_Z"));

		ds_list.SetColumn(ds_list.currow, "NOISE1", ds_list_tmp.getColumn(0, "NOISE1"));		
		ds_list.SetColumn(ds_list.currow, "NOISE2", ds_list_tmp.getColumn(0, "NOISE2"));
		ds_list.SetColumn(ds_list.currow, "NOISE3", ds_list_tmp.getColumn(0, "NOISE3"));
		ds_list.SetColumn(ds_list.currow, "NOISE4", ds_list_tmp.getColumn(0, "NOISE4"));

		ds_list.SetColumn(ds_list.currow, "RAIL_L", ds_list_tmp.getColumn(0, "RAIL_L"));		
		ds_list.SetColumn(ds_list.currow, "RAIL_R", ds_list_tmp.getColumn(0, "RAIL_R"));

		ds_list.SetColumn(ds_list.currow, "BIGO", ds_list_tmp.getColumn(0, "BIGO"));

		obj.fireevent = true;

		ds_list_tmp.DeleteAll();	
	}


	//===============추가===============

	obj.fireevent = false;
	switch( strColumnID ) 
	{
		case "SHKASC_X":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"SHKASC_X") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "SHKASC_Y":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"SHKASC_Y") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}


		case "SHKASC_Z":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"SHKASC_Z") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "SHKDESC_X":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"SHKDESC_X") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "SHKDESC_Y":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"SHKDESC_Y") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "SHKDESC_Z":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"SHKDESC_Z") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "NOISE1":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"NOISE1") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "NOISE2":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"NOISE2") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "NOISE3":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"NOISE3") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
			}

		case "NOISE4":
			if ( varNewValue != ds_list.GetOrgColumn(nRow,"NOISE4") )
			{
				ds_list.SetColumn(nRow, "CHK", '1');
				obj.fireevent = true;
				break;
