/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{

	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}


	var cnt = 0;

	if ( !gfn_isChangeDs(ds_list, true, ""))
	{	
		return;
	}


	ds_list_save.DeleteAll();
	for( var i=0; i<ds_list.rowcount; i++ )
	{

		if (ds_list.GetColumn(i,"CHK") == "1")
		{				
			if ( length(ds_list.GetColumn(i,"POSID")) == 0)
			{				
				gfn_showMsg("CW00001","호기번호");
				return;
			}


			ds_list_save.AddRow();			
			ds_list_save.CopyRow(cnt, "ds_list", i);	 //ds_list_save.CopyRow(0, "ds_list", i);	

			cnt = cnt + 1;

		}

	}




    //선택한 행이 1건 이상일때문 실행
	if ( cnt > 0 )
	{

//trace(ds_list_save.SaveXML());	
//alert("xxx");	
		tit_clearActionInfo();		
		tit_addSelectExActionInfo("ps12:PS1203001A_S4", "ps12:PS1203001A_I1", "ps12:PS1203001A_U1");

		tit_callService(
			""
			, ""
			, "ds_list_save=ds_list_save"
			, ""
			, ""
			, "fn_afterSave"
			, ""
			, ""
			, true

		);			
	}




}





/********************************************************************************
* 기      능   : 저장후 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
//alert("NO");
		gfn_showMsg("CE00001");
	} else {
//alert("YES");
        fn_query();
		gfn_showMsg("CI00003");		

	}
}




