/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	ds_list_save.DeleteAll();
	var cnt = 0;
	for( var i=0; i<ds_list.rowcount; i++ )
	{

		if (ds_list.GetColumn(i,"CHK") == "1"  && ds_list.GetColumn(i,"GBN") != "X" )
		{								

			ds_list_save.AddRow();
			ds_list_save.CopyRow(cnt, "ds_list", i);		

			cnt = cnt + 1;			
		}

	}

	//선택한 행이 1건 이상일때문 실행
	if ( cnt > 0 )
	{
		tit_clearActionInfo();			
		tit_addMultiActionInfo("ps12:PS1203001A_D1");

		tit_callService(
			""
			, ""
			, "ds_list_save=ds_list_save"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			, true  // ""  		
		);


	}
	else
	{
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       		
		global.FRM_BOTTOM.st_msg.value = "대상중 저장상태가 아닌건은 삭제하실수없습니다..";
		return;
	}


}

/********************************************************************************
* 기      능   : 삭제후 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		fn_query();
		gfn_showMsg("CI00004");

	}	
}


