#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var user_grp = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	



	// 방기태 수정 =====================================================
	if (gfn_getCurrDate() >= '20160701')
	{
		gfn_alert("기존 기술지원요청은 종료되었으며 신규 밴드 웹페이지를 이용하시기 바랍니다. "
					+ "\n기술관리팀 이정은SW (02-3670-0911)에게 문의바랍니다.");
		ExecBrowser("http://210.207.246.223:35001/livesupp/adm/band.do?s=2010&band_gbn=1");          	          	         
		fn_close();

		return; 
	}
	//===================================================================







	user_grp = gfn_getUserGroup();

    cal_from.value = substr(gfn_getCurrDate(),0,6)+"01";;  //substr(gfn_getCurrDate(),0,6);
    cal_to.value   = gfn_getCurrDate();  //substr(gfn_getCurrDate(),0,6);



    //if( user_grp == '10' )
    if( user_grp == '10'|| user_grp == '30'|| user_grp == '40') // 설치PM 신청권한 추가.
	{
		cb_lifnr.Enable = false;	
		btn_request.enable = true;  
	}
	else
	{
		cb_lifnr.Enable = true;
		btn_request.enable = false;   
	}



	//fn_searchCode2();

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1204001A_S1");	
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_lifnr=ds_lifnr"
		, ""
		, "fn_afterLifnr"
		, true);

}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	if( user_grp == "10" )
	{
		ds_lifnr.filter("CODE == " + quote( G_VEND_CODE_E) );
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	//else if ( user_grp == "40")
	else if ( user_grp == "30" ||user_grp == "40") // 설치PM 추가
	{
		cb_lifnr.Enable = true;
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL ); //전체
		cb_lifnr.value = "";
	}	
	else
	{
		cb_lifnr.value = "";
		cb_lifnr.Enable = false;
	}

	cb_gbn.Index = 0;

}


/********************************************************************************
* 기      능   : 지원구분 변경시
********************************************************************************/
function cb_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	if(strCode == 'M') 
	{
		ds_sup_cd.Filter("CD=='' || CD=="+quote(strCode));
	}
	else if(strCode == 'E') 
	{
		ds_sup_cd.Filter("CD=='' || CD=="+quote(strCode));
	}
	else
	{
		ds_sup_cd.unFilter();
	}

        cb_sup_cd.Index = 0;
}







/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {

	gfn_clearMsgBar();

	//if(gfn_isNull(cal_from, true, Static0.value)) return;

	// 필수 입력사항 및 일자 체크 	
	if ( !gfn_checkFromToObj(cal_from, cal_to, true, Static0.value, 0, true)   ) 
    {	return; 	}



    // sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps12:PS1204001A_S2");	
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true );    



}




/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}


