var userGroup = "";

//var lv_mrrate1 = 0.0;
var PER_CHK;
var PRE_LIFNR;
var PRE_SUPRDT;
var PRE_PJT;
var PRE_HNO;
var PRE_GBN;
var PRE_SUPCD;
var PRE_GRP;
var lv_finish = '';
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
