function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	//gfn_initForm(true);	
	gfn_initForm();	

	gfn_initDs(ds_master, true);	
	gfn_initDs(ds_cond, true);	


	PER_CHK 	= PER_CHK;
	PRE_LIFNR 	= PRE_LIFNR;
	PRE_SUPRDT 	= PRE_SUPRDT;
	PRE_PJT 	= PRE_PJT;
	PRE_HNO		= PRE_HNO;
    PRE_GBN		= PRE_GBN;
    //PRE_SUPCD   = PRE_SUPCD;
    PRE_GRP		= PRE_GRP;


	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps12:PS1204001B_S2");	
	tit_addSearchActionInfo("ps12:PS1204001B_S7");	
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_lifnr=ds_lifnr ds_error_cd=ds_error_cd"
		, ""
		, ""  //"fn_afterLifnr"
		, ""
		, ""
		, true);    


	if(PER_CHK == "C")
	{

		cb_lifnr.Value 		= PRE_LIFNR;		
		cal_suprdt.Value 	= PRE_SUPRDT;
		ed_pjt.Text			= PRE_PJT;		
		ed_posid.Text 		= PRE_HNO;		
		cb_gbn.Value		= PRE_GBN;


		ufn_enable("C");
		fn_query();

	}
	else if(PER_CHK == "N")
	{				

		cb_lifnr.value = PRE_LIFNR;
		//cb_lifnr.Index = ds_lifnr.FindRow("CODE", PRE_LIFNR);
		cal_suprdt.value   = gfn_getCurrDate();

	    ufn_enable("N");
	}



	ds_closed.ClearData();

	tit_clearActionInfo();		
	tit_addSearchActionInfo("ps12:PS1204001B_S1");     // 마감일
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_closed=ds_closed"
		, ""
		, "" //"fn_afterQuery"
		, ""
		, ""
		, true);

	//마감버튼
	if(ds_closed.GetColumn(0,"CNT") >= 1)
	{

		ufn_enable('F');
		lv_finish = 'X';
	}
	else
	{

		//ufn_enable('I');
	}


	//협력업체일경우
	if( PRE_GRP == '10' )
	{
		cb_lifnr.Enable = false;
		btn_sms.Enable = false;
	}
	else
	{
		btn_sms.Enable = true;
	}


}


