/********************************************************************************
* 기      능   : 컴포넌트들의 활성화 여부
********************************************************************************/	
function ufn_enable(chk)
{
	if(chk=="F")
	{
//PRE_GRP
		cb_lifnr.Enable 	= false;
		cal_suprdt.Enable 	= false;
		ed_pjt.Enable 		= false;
		btn_pjt.Enable      = false;
		cb_gbn.Enable 		= false;
		cb_sup_cd.Enable 	= false;
		//ta_bigo.Enable      = false;
		ta_bigo.Readonly    = true;

		//ed_supman.Enable    = false;
		//cal_supdt.Enable    = false;

		//ed_prwrk.Enable     = false;
		cb_gbn2.Enable 		= false;
		Edit2.Enable        = false;
		Edit0.Enable		= false;
		grd_list.Enable     = false;


		div_btn.btn_query.visible = false;
		div_btn.btn_delete.visible = false;
		div_btn.btn_save.visible = false;     
	}
	else if(chk=="I")
	{   	    

	    //cb_lifnr.Enable 	= true;
		//cal_suprdt.Enable 	= true;
		//ed_pjt.Enable 		= true;
		//btn_pjt.Enable      = true;
		//cb_gbn.Enable 		= true;
		//cb_sup_cd.Enable 	= true;
		//ta_bigo.Enable      = true;
	}
	else if(chk=="C")	
	{

		if( PRE_GRP == '10' || PRE_GRP == '30' || PRE_GRP == '40') //설치PM 추가
		{
			cb_lifnr.Enable 	= false;
			cal_suprdt.Enable 	= false;
			ed_pjt.Enable 		= false;
			btn_pjt.Enable      = false;
			cb_gbn.Enable 		= false;
			cb_sup_cd.Enable 	= true;
			//ta_bigo.Enable      = true;
			ta_bigo.Readonly    = false;

			//ed_supman.Enable    = false;
			//cal_supdt.Enable    = false;

			//ed_prwrk.Enable     = true;
			cb_gbn2.Enable 		= true;

			Edit2.Enable        = true;
			Edit0.Enable		= true;
			grd_list.Enable     = true;


			div_btn.btn_query.visible = false;
			div_btn.btn_delete.visible = true;
			div_btn.btn_save.visible = true;  
		}
		else
		{
			cb_lifnr.Enable 	= false;
			cal_suprdt.Enable 	= false;
			ed_pjt.Enable 		= false;
			btn_pjt.Enable      = false;
			cb_gbn.Enable 		= false;
			cb_sup_cd.Enable 	= false;
			//ta_bigo.Enable      = false;
			ta_bigo.Readonly    = true;

			//ed_supman.Enable    = true;
			//cal_supdt.Enable    = true;

			//ed_prwrk.Enable     = false;
			cb_gbn2.Enable 		= false;

			Edit2.Enable        = false;
			Edit0.Enable		= false;
			grd_list.Enable     = false;

			div_btn.btn_query.visible = false;
			div_btn.btn_delete.visible = false;
			div_btn.btn_save.visible = true;
		}


	}
	else if(chk=="N")
	{

		cb_lifnr.Enable 	= true;
		cal_suprdt.Enable 	= true;
		ed_pjt.Enable 		= false;
		btn_pjt.Enable      = true;
		cb_gbn.Enable 		= true;
		cb_sup_cd.Enable 	= true;
		//ta_bigo.Enable      = true;
		ta_bigo.Readonly    = false;

		//ed_supman.Enable    = false;
		//cal_supdt.Enable    = false;

		//ed_prwrk.Enable     = true;
		cb_gbn2.Enable 		= true;

		Edit2.Enable        = true;
		Edit0.Enable		= true;
		grd_list.Enable     = true;


		div_btn.btn_query.visible = true;
		div_btn.btn_delete.visible = true;
		div_btn.btn_save.visible = true;     
	}
}	



