/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(gfn_isNull(cb_lifnr, true, Static9.Text)) return false; 
	if(gfn_isNull(cal_suprdt, true, Static27.Text)) return false;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false;
	if(gfn_isNull(ed_posid, true, Static11.Text)) return false;
	if(gfn_isNull(cb_gbn, true, Static7.Text)) return false;

	if(gfn_isNull(cb_gbn2, true, Static6.Text)) return false;


	//if(gfn_isNull(cb_sup_cd, true, Static8.Text)) return false;
	//if(gfn_isNull(ed_supman, true, Static12.Text)) return false;




    ds_chk.clearData();
	if(PER_CHK == "N")
	{

		tit_clearActionInfo();		
		tit_addSearchActionInfo("ps12:PS1204001B_S5"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}

	if(ds_chk.GetRowCount() > 0)	
	{
		gfn_showMsg("CW00137");
		return;
	}


	ds_save.ClearData();	
	ds_save.AddRow();	
	ds_save.SetColumn(0, "BIGO", ds_master.GetColumn(0, "BIGO"));

	//ds_save.SetColumn(0, "PROWRK", ds_master.GetColumn(0, "PROWRK"));
	ds_save.SetColumn(0, "PROCD", ds_master.GetColumn(0, "PROCD"));
	ds_save.SetColumn(0, "SUP_CD", ds_master.GetColumn(0, "SUP_CD"));
	//ds_save.SetColumn(0, "SUP_DT", ds_master.GetColumn(0, "SUP_DT"));
	//ds_save.SetColumn(0, "SUP_MAN", ds_master.GetColumn(0, "SUP_MAN"));

	ds_save.SetColumn(0, "PSPID", ds_cond.GetColumn(0, "PSPID"));
	ds_save.SetColumn(0, "POSID", ds_cond.GetColumn(0, "POSID"));	
	ds_save.SetColumn(0, "GBN", ds_cond.GetColumn(0, "GBN"));
	ds_save.SetColumn(0, "LIFNR", ds_cond.GetColumn(0, "LIFNR"));
	ds_save.SetColumn(0, "SUPR_DT", ds_cond.GetColumn(0, "SUPR_DT"));

	ds_save.SetColumn(0, "NAMET2", 			ds_master.GetColumn(0, "NAMET2"));
	ds_save.SetColumn(0, "TEL", 			ds_master.GetColumn(0, "TEL"));
	ds_save.SetColumn(0, "INVERTER_ER", 	ds_master.GetColumn(0, "INVERTER_ER"));
	ds_save.SetColumn(0, "INVERTER_ER2", 	ds_master.GetColumn(0, "INVERTER_ER2"));
	ds_save.SetColumn(0, "INVERTER_ER3", 	ds_master.GetColumn(0, "INVERTER_ER3"));
	ds_save.SetColumn(0, "SYSTEM_ER", 		ds_master.GetColumn(0, "SYSTEM_ER"));
	ds_save.SetColumn(0, "SYSTEM_ER2", 		ds_master.GetColumn(0, "SYSTEM_ER2"));
	ds_save.SetColumn(0, "SYSTEM_ER3", 		ds_master.GetColumn(0, "SYSTEM_ER3"));
	ds_save.SetColumn(0, "CTC_ER", 			ds_master.GetColumn(0, "CTC_ER"));
	ds_save.SetColumn(0, "CTC_ER2", 		ds_master.GetColumn(0, "CTC_ER2"));
	ds_save.SetColumn(0, "CTC_ER3", 		ds_master.GetColumn(0, "CTC_ER3"));



	tit_clearActionInfo();		
	tit_addSelectExActionInfo("ps12:PS1204001B_S6", "ps12:PS1204001B_I1", "ps12:PS1204001B_U1");
	tit_callService(
		""
		, ""
		, "ds_save=ds_save"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);





}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		ufn_enable("C");
		PER_CHK = 'C';  //저장시 변경으로 들어감
		gfn_showMsg("CI00003");		
		//parent.fn_query();
	}
}





/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if(gfn_isNull(cb_lifnr, true, Static9.Text)) return false; 
	if(gfn_isNull(cal_suprdt, true, Static27.Text)) return false;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false;
	if(gfn_isNull(ed_posid, true, Static11.Text)) return false;
	if(gfn_isNull(cb_gbn, true, Static7.Text)) return false;

	if(PER_CHK == "C")
	{
		if (!gfn_showConfirm("CI00015","기술지원 요청서")) 
		{
			return;
		}	


		tit_clearActionInfo();	
		//tit_addSingleActionInfo("bs04:BS0409002B_D2");
		tit_addSingleActionInfo("ps12:PS1204001B_D1");
		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }

	//fn_reset("D");

}




/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00004");		
		parent.fn_query();    
		fn_close();
	}
}




function cb_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == 'M') 
	{		
		ds_sup_cd.Filter("CD=="+quote(strCode));
	}
	else if(strCode == 'E') 
	{
		ds_sup_cd.Filter("CD=="+quote(strCode));
	}
	else
	{
		ds_sup_cd.unFilter();
	}

	//cb_sup_cd.Index = 0;
}

