function fn_query() 
{	

	if(gfn_isNull(div_pop.cb_lifnr, true, div_pop.Static9.Text)) return;
	if(gfn_isNull(div_pop.cal_suprdt, true, div_pop.Static27.Text)) return;
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.Static1.Text)) return;		
	if(gfn_isNull(div_pop.ed_posid, true, div_pop.Static11.Text)) return;		
	if(gfn_isNull(div_pop.cb_gbn, true, div_pop.Static7.Text)) return;

	//if(gfn_isNull(ed_supman, true, Static12.Text)) return;
	//if(gfn_isNull(cal_supdt, true, Static14.Text)) return;			
	//if(gfn_isNull(cb_finish, true, Static15.Text)) return;

//ALERT("XXX");	
//========================> 여기서 부터	
	if(PER_CHK == "A")
	{		  	
		tit_clearActionInfo();		   
		//tit_addSearchActionInfo("ps12:PS1204001B_S3");         		    
		tit_addSearchActionInfo("ps12:PS1204002B_S2");         		    

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);

	}
    else if(PER_CHK == "B")
    {		        	
		tit_clearActionInfo();		  		     
		//tit_addSearchActionInfo("ps12:PS1204001B_S4");         		      
		tit_addSearchActionInfo("ps12:PS1204002B_S3");         		      
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
